    CODE_TEXT = #{codeText,jdbcType=VARCHAR},
  
  
    DESCRIPTION = #{description,jdbcType=VARCHAR},
  

where TYPE = #{type,jdbcType=DECIMAL}
  and CODE = #{code,jdbcType=VARCHAR}

