mapper.select:
select line_id,station_id,dev_type_id,device_id,csc_num,array_id,concourse_id,ip_address,store_id,
dev_serial,record_flag,version_no,to_char(config_date,'yyyy-mm-dd') config_date,dev_name
from W_ACC_ST.W_OP_PRM_DEV_CODE_ACC where record_flag=#{record_flag,jdbcType=CHAR} and version_no=#{version_no,jdbcType=CHAR}  

    and line_id =#{line_id,jdbcType=CHAR} 


    and station_id =#{station_id,jdbcType=CHAR} 


    and dev_type_id =#{dev_type_id,jdbcType=CHAR}


    and device_id =#{device_id,jdbcType=CHAR}


    and dev_serial =#{dev_serial,jdbcType=CHAR}

order by line_id,station_id,dev_type_id,device_id

select line_id,station_id,dev_type_id,device_id,csc_num,array_id,concourse_id,ip_address,store_id,
dev_serial,record_flag,version_no,to_char(config_date,'yyyy-mm-dd') config_date,dev_name
from W_ACC_ST.W_OP_PRM_DEV_CODE_ACC where record_flag=#{record_flag,jdbcType=CHAR} and version_no=#{version_no,jdbcType=CHAR}  

                                            and line_id =#{line_id,jdbcType=CHAR} 


                                            and station_id =#{station_id,jdbcType=CHAR} 

                                      and dev_type_id =#{dev_type_id,jdbcType=CHAR} and device_id =#{device_id,jdbcType=CHAR}

mapper.update:
update W_ACC_ST.W_OP_PRM_DEV_CODE_ACC set csc_num=#{csc_num},
            array_id=#{array_id},concourse_id=#{concourse_id},ip_address=#{ip_address},store_id=#{store_id,jdbcType=CHAR},
            dev_serial=#{dev_serial},config_date=to_date(#{config_date},'yyyy-mm-dd'),dev_name=#{dev_name}
            where 1=1 

                    and line_id =#{line_id,jdbcType=CHAR} 


                    and station_id =#{station_id,jdbcType=CHAR} 

                and dev_type_id=#{dev_type_id,jdbcType=CHAR} and device_id=#{device_id,jdbcType=CHAR} 
                and record_flag=#{record_flag,jdbcType=CHAR} and version_no=#{version_no,jdbcType=CHAR}

mapper.delete:
delete from W_ACC_ST.W_OP_PRM_DEV_CODE_ACC where 1=1

            and line_id =#{line_id,jdbcType=CHAR} 


            and station_id =#{station_id,jdbcType=CHAR} 

        and dev_type_id=#{dev_type_id,jdbcType=CHAR} 
        and device_id=#{device_id,jdbcType=CHAR} and record_flag=#{record_flag,jdbcType=CHAR} and version_no=#{version_no,jdbcType=CHAR}
