mapper.select:
select version_no,record_flag,b_line_id,b_station_id,e_line_id,
       e_station_id,contc_id,in_percent 
from   w_acc_st.w_op_prm_deal_assign_contc
where  record_flag=#{record_flag} and version_no=#{version_no}  

    and b_line_id =#{b_line_id} 


    and b_station_id =#{b_station_id} 


    and e_line_id =#{e_line_id} 


    and e_station_id =#{e_station_id} 


    and contc_id =#{contc_id} 

order by b_line_id,b_station_id,e_line_id,e_station_id,contc_id

select version_no,record_flag,b_line_id,b_station_id,e_line_id,
        e_station_id,contc_id,in_percent 
 from   w_acc_st.w_op_prm_deal_assign_contc
 where  record_flag=#{record_flag} and version_no=#{version_no} 
        and b_line_id =#{b_line_id} and b_station_id =#{b_station_id} and e_line_id =#{e_line_id}
 and e_station_id =#{e_station_id} and contc_id =#{contc_id}

mapper.update:
update w_acc_st.w_op_prm_deal_assign_contc set in_percent=#{in_percent}
            where  record_flag=#{record_flag} and version_no=#{version_no} 
       and b_line_id =#{b_line_id} and b_station_id =#{b_station_id} and e_line_id =#{e_line_id}
and e_station_id =#{e_station_id} and contc_id =#{contc_id}

mapper.delete:
    delete from w_acc_st.w_op_prm_deal_assign_contc where record_flag=#{record_flag} and version_no=#{version_no} 
       and b_line_id =#{b_line_id} and b_station_id =#{b_station_id} and e_line_id =#{e_line_id}
and e_station_id =#{e_station_id} and contc_id =#{contc_id}
