mapper.select:
   

    select  *
    from w_acc_st.W_OP_PRM_DEGRADE_MODE_RECD
    where LINE_ID = #{lineId,jdbcType=CHAR}  and STATION_ID = #{stationId,jdbcType=CHAR}
    and DEGRADE_MODE_ID = #{degradeModeId,jdbcType=CHAR} and START_TIME = #{startTime,jdbcType=CHAR}
    and  END_TIME = #{endTime,jdbcType=CHAR}

select 

from W_ACC_ST.W_OP_PRM_DEGRADE_MODE_RECD
where 1=1

    and LINE_ID = #{lineId,jdbcType=CHAR}


    and STATION_ID = #{stationId,jdbcType=CHAR}


    and DEGRADE_MODE_ID = #{degradeModeId,jdbcType=CHAR}


    and HDL_FLAG = #{hdlFlag,jdbcType=CHAR}


    and WATER_NO = #{waterNo,jdbcType=DECIMAL}


    and VERSION_NO = #{versionNo,jdbcType=CHAR}
