mapper.select:
select status_id,status_value,status_name,acc_status_name
from W_ACC_ST.W_OP_COD_COM_STATUS_MAPPING where 1=1

    and status_id=#{status_id} 


    and status_value=#{status_value}     


    and acc_status_value=#{acc_status_value}     

order by status_id,status_value

select status_id,status_value,status_name,acc_status_name
from W_ACC_ST.W_OP_COD_COM_STATUS_MAPPING
where status_id=#{status_id} and status_value=#{status_value}
