mapper.select:
select 

from W_IC_COD_CARD_TYPE_CONTRAST
where IC_MAIN_TYPE = #{icMainType,jdbcType=CHAR}
  and IC_SUB_TYPE = #{icSubType,jdbcType=CHAR}

mapper.delete:
delete from W_IC_COD_CARD_TYPE_CONTRAST
where IC_MAIN_TYPE = #{icMainType,jdbcType=CHAR}
  and IC_SUB_TYPE = #{icSubType,jdbcType=CHAR}

mapper.update:
update W_IC_COD_CARD_TYPE_CONTRAST

  
    CARD_MAIN_TYPE = #{cardMainType,jdbcType=CHAR},
  
  
    CARD_SUB_TYPE = #{cardSubType,jdbcType=CHAR},
  
  
    BOX_UNIT = #{boxUnit,jdbcType=DECIMAL},
  

where IC_MAIN_TYPE = #{icMainType,jdbcType=CHAR}
  and IC_SUB_TYPE = #{icSubType,jdbcType=CHAR}

update W_IC_COD_CARD_TYPE_CONTRAST
set CARD_MAIN_TYPE = #{cardMainType,jdbcType=CHAR},
  CARD_SUB_TYPE = #{cardSubType,jdbcType=CHAR},
  BOX_UNIT = #{boxUnit,jdbcType=DECIMAL}
where IC_MAIN_TYPE = #{icMainType,jdbcType=CHAR}
  and IC_SUB_TYPE = #{icSubType,jdbcType=CHAR}
