CREATE OR REPLACE PROCEDURE ACC_ST.up_dm_st_his_mv1(out_retResult OUT INTEGER, --返回结果
                                                    out_msg       OUT VARCHAR2 --返回信息
                                                    ) --输入的队列表名
  ---------------------------------------------------------------------------------
  --过程名：  up_dm_st_his_mv1
  --功能描述：中央清算：单程票发售、钱包交易、进站、更新数据倒历史表
  --输出参数  ：out_retResult 模块返回值 out_msg 返回信息
  --返回值    ：0：操作成功；其他：操作失败；
  --创建者：  张建华
  --创建日期：20131020
  --修改日期： 20150424 by mqf
  --修改内容： 按日期遍历，记录数大于分表最大记录数则停止迁移数据，新建分表再迁移数据。
             --解决上线运行时，堆积大量的记录插入到第一个分表的问题
   --修改日期：20160704 modify by mqf
   --修改内容：1.数据迁移到分表同时插入清算流水号记录到清算流水号索引表ST_IDX_TRX_HISTORY_BALANCE,
   --         2.关闭游标
   --修改日期：20170123 add by mqf
   --修改内容：增加公交消费历史表迁移到分表,公交消费历史表统计的清算流水号也插入清算流水号索引表ST_IDX_TRX_HISTORY_BALANCE
   --修改日期：20170321 modify by mqf
   --修改内容：优化，不使用内函数to_char，改为值to_date转换
  -------------------------------------------------------------------------------
 AS

  v_msg            VARCHAR2(50);
  v_sql            varchar2(5000);
  v_sql2           varchar2(5000);
  v_new_end        date; --新的结束时间
  v_min_time       date; --buf表中的最小交易时间
  v_max_time       date; --buf表中的最大交易时间
  v_min_table      VARCHAR2(30); --最小交易时间对应的最小的历史表名
  v_max_table      VARCHAR2(30); --最大的历史表
  v_count          number(18);
  v_deal_type      char(2); --交易类型  50, 53, 54,56
  v_time_type      VARCHAR2(20);
  v_column         VARCHAR2(2000);
  v_tmp1           VARCHAR2(256);
  v_cost_seconds   number;
  begin_time       date; --迁移开始时间
  keep_days        int; --历史表数据保存天数
  end_clear_time   VARCHAR2(20);
  begin_clear_time VARCHAR2(20);
  v_in_que         VARCHAR2(50);
  v_table_sql      varchar2(5000); --sql语句
  v_begin_date     date;
  v_where_sql      varchar2(500);

  --20140424 add by mqf
  v_recd_count integer; --索引表已更新记录数
  divide_recd_count integer; --配置表最大记录数
  v_rowcount  integer; --更新记录数
  v_time_type_value varchar2(20); --日期
  TYPE MYCURSOR IS REF CURSOR;  --定义游标类型
  cur_date_type MYCURSOR;       --定义游标
  v_datetime_begin     varchar2(20); --遍历的开始时间
  v_datetime_end     varchar2(20); --遍历的结束时间
  v_datetime_where_sql  varchar2(2048);

  --20160704
  v_idx_balance_count  integer; --插入清算流水号索引表ST_IDX_TRX_HISTORY_BALANCE记录数
BEGIN
  declare
    cursor scc_table is
      select t.origin_table_name tmp_origin_table,
             t.create_sql        tmp_create_sql,
             t.is_squadday       tmp_is_squadday,
             t.ab_name           tmp_ab_name,
             t.table_columns     tmp_table_columns,
             t.date_type         tmp_date_type,
             t.keep_days         tmp_keep_days,
       t.divide_recd_count tmp_divide_recd_count
        from acc_st.st_cfg_clear_table t
       where t.db_type = 'trx';
  begin
    for i_cur in scc_table loop

      v_time_type := i_cur.tmp_date_type; --时间类型
      v_in_que    := i_cur.tmp_origin_table; --源表 已包含用户前缀acc_st_his
      v_table_sql := i_cur.tmp_create_sql; --建表语句
      v_column    := i_cur.tmp_table_columns;
      keep_days   := i_cur.tmp_keep_days;
    --20150424 add by mqf
    divide_recd_count := trim(i_cur.tmp_divide_recd_count);
    --20150424 modify by mqf 屏蔽未使用变量
    /*
      --取交易数据中最小的交易时间
      v_tmp1 := ' select min(' || v_time_type || ') from ' || v_in_que;
      execute immediate v_tmp1
        into v_min_time;

      --取交易数据中最大的交易时间
      v_tmp1 := 'select max(' || v_time_type || ') from ' || v_in_que;
      execute immediate v_tmp1
        into v_max_time;
    */

	  v_deal_type := ''; --20170123 add by mqf 默认为空

      if v_in_que = 'acc_st_his.st_his_deal' then
        --钱包交易st_his_deal
        v_deal_type := '54';
      elsif v_in_que = 'acc_st_his.st_his_entry' then
        --进站交易 st_his_entry
        v_deal_type := '53';
      elsif v_in_que = 'acc_st_his.st_his_sale_sjt' then
        --单程票发售 st_buf_sale_sjt
        v_deal_type := '50';
      elsif v_in_que = 'acc_st_his.st_his_update' then
        --更新交易st_buf update
        v_deal_type := '56';
	  elsif v_in_que = 'acc_st_his.st_ext_mtr_his_deal' then --20170123 add by mqf 增加公交消费历史表
        v_deal_type := '94';
      end if;
      begin
        select min(table_name)
          into v_min_table
          from acc_st.st_idx_trx_history
         where data_type = 'trx'
           and recd_type = v_deal_type;
      exception
        when OTHERS THEN
          begin
            out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                             dbms_utility.format_error_backtrace() || ']行';
            out_retresult := sqlcode;
            continue;
          end;
      end;
      if v_min_table is null then
        continue;
      end if;

      select max(table_name)
        into v_max_table
        from acc_st.st_idx_trx_history
       where data_type = 'trx'
         and recd_type = v_deal_type;

      --定义取出所有需要导入数据的His表的游标
      --(最大的历史表需要特殊处理,更改st_idx_trx_history表中对应的结束时间)
      declare
        cursor cu_table is
          select table_name,
                 to_char(begin_date, 'yyyy-mm-dd hh24:mi:ss') begin_date,
                 to_char(end_date, 'yyyy-mm-dd hh24:mi:ss') end_date
            from acc_st.st_idx_trx_history
           where data_type = 'trx'
             and recd_type = v_deal_type
             and table_name >= v_min_table
             and table_name < v_max_table;
      begin
        for my_cur in cu_table loop

          v_tmp1 := 'lock table acc_st_his.' || my_cur.table_name ||
                    ' in  exclusive mode nowait';
          execute immediate v_tmp1;

          begin_time  := sysdate;
          v_where_sql := ' where ' || v_time_type || ' <= to_date(' || '''' ||
                         my_cur.end_date ||
                         ''',''yyyy-mm-dd hh24:mi:ss'') and ' ||
                         v_time_type || ' >= to_date(' || '''' ||
                         my_cur.begin_date ||
                         ''',''yyyy-mm-dd hh24:mi:ss'')';
          v_sql       := 'select count(*) from ' || v_in_que || v_where_sql;
          execute immediate v_sql
            into v_count;

          --需迁移的数据等于0退出本次循环
          if 0 = v_count then
            continue;
          end if;

          --把数据按交易时间插入对应的历史表
          v_sql := 'insert into acc_st_his.' || my_cur.table_name || '(' ||
                   v_column || ') select ' || v_column || ' from ' ||
                   v_in_que || v_where_sql;

          execute immediate v_sql;

          --删除源表数据
      /*
          v_sql := 'delete from ' || v_in_que || v_where_sql;
          execute immediate v_sql;
      */

          v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
          out_msg        := '从' || v_in_que || '表插入数据到' ||
                            my_cur.table_name || '，共' || v_count || '条';
          acc_st.up_dm_st_mv_log(v_in_que,
                          my_cur.table_name,
                          '0000000000',
                          begin_time,
                          sysdate,
                          v_cost_seconds,
                          v_count,
                          out_msg,
                          v_sql);--20150520 modify by mqf v_sql2改为v_sql

          update acc_st.st_idx_trx_history
             set recd_count = recd_count + v_count
           where table_name = my_cur.table_name;

          --20160704 add by mqf 插入清算流水号索引表ST_IDX_TRX_HISTORY_BALANCE
		  begin_time  := sysdate;
		  v_sql := 'insert into acc_st.ST_IDX_TRX_HISTORY_BALANCE (table_name,data_type,recd_type,balance_water_no,create_time) '
					|| ' select ''' || my_cur.table_name || ''', ''trx'', ''' || v_deal_type || ''', balance_water_no, sysdate from ' || v_in_que || v_where_sql
					|| ' and not exists(select 1 from acc_st.ST_IDX_TRX_HISTORY_BALANCE '
					|| '    where table_name='''|| my_cur.table_name || ''' and balance_water_no = ' || v_in_que || '.balance_water_no )'
					|| ' group by balance_water_no';
		  execute immediate v_sql;
		  v_idx_balance_count := sql%rowcount;

		  if v_idx_balance_count >0 then
			  v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
			  out_msg        := '从' || v_in_que || '表插入数据到acc_st.ST_IDX_TRX_HISTORY_BALANCE，共' || v_idx_balance_count || '条';
			  acc_st.up_dm_st_mv_log(v_in_que,
							  'acc_st.ST_IDX_TRX_HISTORY_BALANCE',
							  '0000000000',
							  begin_time,
							  sysdate,
							  v_cost_seconds,
							  v_idx_balance_count,
							  out_msg,
							  v_sql);
		  end if;
          --end 20160704


      begin_time  := sysdate;  --20150520 add by mqf
      --删除源表数据
          v_sql := 'delete from ' || v_in_que || v_where_sql;
          execute immediate v_sql;

      --20150520 add by mqf
      out_msg := '从' || v_in_que || '删除' || v_count || '条数据';
      v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);

      acc_st.up_dm_st_mv_log(v_in_que,
              '0',
              '0000000000',
              begin_time,
              sysdate,
              v_cost_seconds,
              v_count,
              out_msg,
              v_sql);

        end loop;
        commit;
      end;

      --对最大的历史表导入数据
    --20150424 是否应放在下面，insert前面，有可能记录为0
      v_tmp1 := 'lock table acc_st_his.' || v_max_table ||
                ' in exclusive mode';
      execute immediate v_tmp1;

      end_clear_time := to_char(sysdate - keep_days,
                                'yyyy-mm-dd hh24:mi:ss');
      v_new_end      := to_date(substr(end_clear_time, 1, 10) ||
                                ' 23:59:59',
                                'yyyy-mm-dd hh24:mi:ss');
      end_clear_time := to_char(v_new_end, 'yyyy-mm-dd hh24:mi:ss');
      --20150424 modify by mqf 增加查询recd_count
      select begin_date,recd_count
        into v_begin_date,v_recd_count
        from acc_st.st_idx_trx_history
       where table_name = v_max_table; --迁移的总记录数
      begin_clear_time := to_char(v_begin_date, 'yyyy-mm-dd hh24:mi:ss');
	  /*
      v_where_sql      := ' where to_char(' || v_time_type ||
                          ',''yyyy-mm-dd hh24:mi:ss'')  >= ''' ||
                          begin_clear_time || '''  and  to_char(' ||
                          v_time_type ||
                          ',''yyyy-mm-dd hh24:mi:ss'')  <=''' ||
                          end_clear_time || '''';
						  */
	  --20170321 modify by mqf 优化，不使用内函数to_char，改为值to_date转换
	  v_where_sql      := ' where ' || v_time_type || ' >= to_date(' || '''' ||
                         begin_clear_time ||
                         ''',''yyyy-mm-dd hh24:mi:ss'') and ' ||
                         v_time_type || ' <= to_date(' || '''' ||
                         end_clear_time ||
                         ''',''yyyy-mm-dd hh24:mi:ss'')';

      --索引表已迁移记录大于或等于分表最大记录数，将跳到一次循环 20150520 modify by mqf
      if v_recd_count>=divide_recd_count then
      continue;
    end if;

      v_sql2 := 'select count(*) from ' || v_in_que || v_where_sql;
      execute immediate v_sql2
        into v_count;
      --迁移的总记录数等于0退出本次循环
      if 0 = v_count then
        continue;
      end if;

    --20150424 add by mqf 按日期遍历，解决上线运行时，堆积大量的记录插入到第一个分表中。
    --'yyyyMMdd'
    --20150424 add by mqf 设置begin_time
    begin_time := sysdate;
    v_sql := ' select distinct to_char(' || v_time_type || ', ''yyyy-mm-dd'') from ' || v_in_que || v_where_sql
        || ' order by to_char(' || v_time_type || ', ''yyyy-mm-dd'')';
    --更新记录数初始为0
    v_rowcount := 0;

    open cur_date_type for v_sql;
    loop
      fetch cur_date_type into v_time_type_value;
      exit when cur_date_type%notfound;

      --超过分表最大记录数，将结束循环
      if (v_recd_count+v_rowcount>=divide_recd_count) then
        exit;
      end if;
      v_datetime_begin := v_time_type_value || ' 00:00:00';
      v_datetime_end := v_time_type_value || ' 23:59:59';
	  /*
      v_datetime_where_sql     := ' where to_char(' || v_time_type ||
                          ',''yyyy-mm-dd hh24:mi:ss'')  >= ''' ||
                          v_datetime_begin || '''  and  to_char(' ||
                          v_time_type ||
                          ',''yyyy-mm-dd hh24:mi:ss'')  <=''' ||
                          v_datetime_end || '''';
						  */

	  --20170321 modify by mqf 优化，不使用内函数to_char，改为值to_date转换
	  v_datetime_where_sql     := ' where ' || v_time_type || ' >= to_date(' || '''' ||
                         v_datetime_begin ||
                         ''',''yyyy-mm-dd hh24:mi:ss'') and ' ||
                         v_time_type || ' <= to_date(' || '''' ||
                         v_datetime_end ||
                         ''',''yyyy-mm-dd hh24:mi:ss'')';

      v_sql := ' insert into acc_st_his.' || v_max_table || '(' || v_column || ')' ||
           ' select ' || v_column || ' from ' || v_in_que ||
           v_datetime_where_sql;


      execute immediate v_sql;
      --累加更新记录数
      v_rowcount := v_rowcount + sql%rowcount;
    end loop;
    close cur_date_type; --20160901 add by mqf 关闭游标

    --20150424 add by mqf v_whereSql用于删除记录
	/*
    v_where_sql      := ' where to_char(' || v_time_type ||
                          ',''yyyy-mm-dd hh24:mi:ss'')  >= ''' ||
                          begin_clear_time || '''  and  to_char(' ||
                          v_time_type ||
                          ',''yyyy-mm-dd hh24:mi:ss'')  <=''' ||
                          v_datetime_end || '''';
						  */

    --20170321 modify by mqf 优化，不使用内函数to_char，改为值to_date转换
    v_where_sql      := ' where ' || v_time_type || ' >= to_date(' || '''' ||
						 begin_clear_time ||
						 ''',''yyyy-mm-dd hh24:mi:ss'') and ' ||
						 v_time_type || ' <= to_date(' || '''' ||
						 v_datetime_end ||
						 ''',''yyyy-mm-dd hh24:mi:ss'')';
    v_new_end := to_date(v_datetime_end, 'yyyy-mm-dd hh24:mi:ss');
    v_count := v_rowcount;


    --20150424 by mqf 屏蔽
      --begin_time := sysdate;

      v_cost_seconds := round(to_number(sysdate - begin_time) * 24 * 60 * 60);
      out_msg        := '从' || v_in_que || '表插入数据到' || v_max_table || '，共' ||
                        v_count || '条';

      acc_st.up_dm_st_mv_log(v_in_que,
                      v_max_table,
                      '0000000000',
                      begin_time,
                      sysdate,
                      v_cost_seconds,
                      v_count,
                      out_msg,
                      v_sql); --20150424 out_msg 改为 v_sql


    if v_count >0 then --20150512 add by mqf 记录大于0才更新索引表、删除源表数据
        --更新st_idx_trx_history表信息
      update acc_st.st_idx_trx_history
       set recd_count = recd_count + v_count, end_date = v_new_end
       where table_name = v_max_table;

      --20160704 add by mqf 最大的历史表  插入清算流水号索引表ST_IDX_TRX_HISTORY_BALANCE
	  begin_time  := sysdate;
	  v_sql := 'insert into acc_st.ST_IDX_TRX_HISTORY_BALANCE (table_name,data_type,recd_type,balance_water_no,create_time) '
				|| ' select ''' || v_max_table || ''', ''trx'', ''' || v_deal_type || ''', balance_water_no, sysdate from ' || v_in_que || v_where_sql
				|| ' and not exists(select 1 from acc_st.ST_IDX_TRX_HISTORY_BALANCE '
				|| '    where table_name='''|| v_max_table || ''' and balance_water_no = ' || v_in_que || '.balance_water_no )'
				|| ' group by balance_water_no';

	  execute immediate v_sql;
	  v_idx_balance_count := sql%rowcount;

	  if v_idx_balance_count >0 then
		  v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
		  out_msg        := '从' || v_in_que || '表插入数据到acc_st.ST_IDX_TRX_HISTORY_BALANCE，共' || v_idx_balance_count || '条';
		  acc_st.up_dm_st_mv_log(v_in_que,
						  'acc_st.ST_IDX_TRX_HISTORY_BALANCE',
						  '0000000000',
						  begin_time,
						  sysdate,
						  v_cost_seconds,
						  v_idx_balance_count,
						  out_msg,
						  v_sql);
	  end if;
      --end 20160704


      ---------删除历史数据开始--------------------------------------------
      begin_time := sysdate;

      --删除操作
      v_sql := 'delete from  ' || v_in_que || v_where_sql;
      execute immediate v_sql;

      --记录日志
      out_msg := '从' || v_in_que || '删除' || v_count || '条数据';

