CREATE OR REPLACE PROCEDURE ACC_ST.up_dm_st_rpt_create1(out_retResult OUT INTEGER, --返回结果
                                                 out_msg       OUT VARCHAR2 --返回信息
                                                 )
---------------------------------------------------------------------------------
  --过程名：  up_dm_st_rpt_create1
  --功能描述：建立中央清算各中间表的历史分表
  --输出参数  ：out_retResult 模块返回值 out_msg 返回信息
  --返回值    ：0：操作成功；其他：操作失败；out_retResult为0时，out_msg返回表名
  --创建者：  张建华
  --创建日期：201301023
  --修改：
  -------------------------------------------------------------------------------
 AS

  v_msg      varchar(50);
  v_count    number(18); --临时统计变量
  v_flag     number(1); --创建0001的标志，0：创建
  v_time     varchar2(20);
  v_count2   number(18); --临时统计变量
  v_min_time varchar2(10); --最小运营时间
  -- v_max_time     varchar2(10); --最大运营时间
  v_temp_sql     varchar2(4000); --临时sql语句
  v_temp_num     varchar2(6); --分表序列号 --20150428 modify by mqf 改为6位
  v_table_sql    varchar2(4000); --建表临时sql语句
  new_table_name varchar2(40); --分表表名
  --v_min_balance_no varchar2(10); --最大清算流水号
  --v_max_balance_no varchar2(10); -- 最小清算流水
BEGIN
  begin
    out_msg       := '建表成功';
    out_retResult := sqlcode;
    declare
      cursor scc_table is
        select t.origin_table_name tmp_origin_table, --包含用户前缀acc_st
               t.create_sql        tmp_create_sql,
               t.is_squadday       tmp_is_squadday,
               t.ab_name           tmp_ab_name
          from acc_st.st_cfg_clear_table t
         where t.db_type = 'rpt'
           and t.is_squadday = 1; --有运营日标志
    begin
      for i_cur in scc_table loop
        begin
          v_flag := 2;
          --因为有些原表名太长，所以生成的分表采用自定义的表名，控制了长度
          --new_table_name := i_cur.tmp_ab_name || '_0001';
      new_table_name := i_cur.tmp_ab_name || '000001'; --20150426 modify by mqf 统计使用6位数字
          begin
            v_table_sql := 'select count(*)  from ' ||
                           i_cur.tmp_origin_table;
            execute immediate v_table_sql
              into v_count;
            if 0 = v_count then
              continue;
            end if;
          end;

          begin
            select count(*)
              into v_count
              from acc_st.st_idx_rpt_history
             where table_name like i_cur.tmp_ab_name || '_%';
          end;
          begin

            v_temp_sql := 'select count(*) from dba_objects where object_name = upper (''' ||
                          new_table_name ||
                          ''') and owner=''ACC_ST'' and object_type =''TABLE''';
            execute immediate v_temp_sql
              into v_count2;
          end;
        exception
          when OTHERS THEN
            begin
              out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                               dbms_utility.format_error_backtrace() ||
                               ']行,' || new_table_name || ',' ||
                               v_table_sql;
              out_retResult := sqlcode;
              acc_st.up_dm_st_mv_log(i_cur.tmp_origin_table,
                              new_table_name,
                              '',
                              sysdate,
                              sysdate,
                              0,
                              0,
                              '创建' || new_table_name || '失败',
                              out_msg);
              continue;
            end;
        end;

        begin
          if v_count = 0 then
            --st_idx_rpt_history没有对应分表的记录
            if v_count2 > 0 then
              --分表的物理表存在
              v_temp_sql := 'drop table acc_st.' || new_table_name;
              execute immediate v_temp_sql; --删除0001分表表

            end if;
            v_flag := 0;

          else
            ----st_idx_rpt_history有对应分表的记录
            if v_count2 = 0 then
              --分表的物理表不存在
              v_flag := 0;
            end if;
          end if;

        end;
        if v_flag = 0 then
          --先建表，再初始化
          begin
            --建0001表
            begin
              --获取建表语句
              --替换表名
              select regexp_replace(i_cur.tmp_create_sql,
                                    '%s',
                                    new_table_name)
                into v_temp_sql
                from dual;

              --每个建表语句中含有建索引、主键信息，且以；分割
              declare
                cursor sql_cur is
                  select * from table(acc_st.up_dm_st_splitstr(v_temp_sql, ';'));
              begin
                for t_cur in sql_cur loop
                  v_table_sql := t_cur.column_value;
                  if trim(v_table_sql) is not null and v_table_sql <> ' ' then
                    execute immediate v_table_sql; --创建0001分表相关的操作
                  end if;
                end loop;
              end;
            end;

      --20150512 add by mqf 增加日志
      acc_st.up_dm_st_mv_log(i_cur.tmp_origin_table,
                new_table_name,
                '',
                sysdate,
                sysdate,
                0,
                0,
                '成功创建' || new_table_name || '表',
                'create table ' || new_table_name || '...');

            select to_char(sysdate - 1, 'yyyymmdd') into v_time from dual;
            /**
            --计算最小、最大流水号、最小运营日，最大运营日
            v_temp_sql := 'select min(t.squad_day)  from ' ||
                          i_cur.tmp_origin_table ||
                          ' t where t.balance_water_no <=''' || v_time || '''';
            execute immediate v_temp_sql
              into v_min_time;**/

            v_min_time := '20130501'; --数据总有延迟，设定一个初始时间

            select count(*)
              into v_count
              from acc_st.st_idx_rpt_history
             where table_name = new_table_name;
            if v_count = 0 then
              insert into acc_st.st_idx_rpt_history
                (table_name,
                 origin_table_name,
                 min_squad_day,
                 max_squad_day,
                 recd_count,
                 create_time)
              values
                (new_table_name,
                 i_cur.tmp_origin_table,
                 v_min_time,
                 '',
                 0,
                 sysdate);
            end if;

          exception
            when OTHERS THEN
              begin
                out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                                 dbms_utility.format_error_backtrace() ||
                                 ']行,' || new_table_name;
                out_retResult := sqlcode;
                acc_st.up_dm_st_mv_log(i_cur.tmp_origin_table,
                                new_table_name,
                                '',
                                sysdate,
                                sysdate,
                                0,
                                0,
                                '创建' || new_table_name || '失败',
                                out_msg);
                continue;
              end;
        /* 20150512 modify by mqf 屏蔽
              up_dm_st_mv_log(i_cur.tmp_origin_table,
                              new_table_name,
                              '',
                              sysdate,
                              sysdate,
                              0,
                              0,
                              '创建' || new_table_name || '成功',
                              out_msg);
                */

          end;
        end if;
      end loop;

    end;

  end;

  --以下为创建非0001表
  begin
    declare
      cursor cu_table is
        select regexp_substr(a.table_name, '.{1,6}$') tmp_i_list_no, --'20150426 modify by mqf 统计使用6位数字 '.{1,4}$' 改为 '.{1,6}$'
               a.recd_count tmp_recd_count,
               --to_char(to_date(substr(a.end_balance_no, 0, 8), 'yyyymmdd') + 1, 'yyyymmdd') || '01' tmp_max_balance_no,
               to_char(to_date(a.max_squad_day, 'yyyymmdd') + 1, 'yyyymmdd') tmp_max_squad_day,
               a.origin_table_name tmp_origin_table,
               b.divide_recd_count tmp_divide_recd,
               b.create_sql tmp_create_sql,
               b.is_squadday tmp_is_squadDay,
               b.ab_name tmp_ab_name
          from acc_st.st_idx_rpt_history a, acc_st.st_cfg_clear_table b
         where a.origin_table_name = b.origin_table_name
           and b.db_type = 'rpt'
           and b.is_squadday = 1
           and a.table_name in
               (select max(table_name)
                  from st_idx_rpt_history
                 group by origin_table_name);

    begin
      for my_cur in cu_table loop
        begin
          begin
            if my_cur.tmp_recd_count >= my_cur.tmp_divide_recd then
              --达到配置表中的最大记录数，需要建表
              --下一分表的序列号
              v_temp_num     := up_dm_st_tableno(my_cur.tmp_i_list_no);
              new_table_name := my_cur.tmp_ab_name || v_temp_num; --20150428 modify by mqf 去掉 '_'
              begin
                v_temp_sql := 'select count(*) from user_objects where object_name = upper (''' ||
                              new_table_name || ''')';
                execute immediate v_temp_sql
                  into v_count2;
                if v_count2 > 0 then
                  --删除该表
                  v_temp_sql := 'drop table  acc_st.' || new_table_name;
                  execute immediate v_temp_sql;
                end if;
              end;

              --获取建表语句
              --替换表名
              select REGEXP_REPLACE(my_cur.tmp_create_sql,
                                    '%s',
                                    new_table_name)
                into v_table_sql
                from dual;

              --每个建表语句中含有建索引、主键信息，且以；分割
              declare
                cursor sql_cur is
                  select * from table(acc_st.up_dm_st_splitstr(v_table_sql, ';'));
              begin
                for t_cur in sql_cur loop
                  v_temp_sql := t_cur.column_value;
                  if trim(v_temp_sql) is not null and v_temp_sql <> ' ' then
                    execute immediate v_temp_sql; --创建0001分表相关的操作
                  end if;
                end loop;

              exception
                when OTHERS THEN
                  begin
                    out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                                     dbms_utility.format_error_backtrace() ||
                                     ']行,' || new_table_name;
                    out_retResult := sqlcode;
                    acc_st.up_dm_st_mv_log(my_cur.tmp_origin_table,
                                    new_table_name,
                                    '',
                                    sysdate,
                                    sysdate,
                                    0,
                                    0,
                                    '创建' || new_table_name || '失败',
                                    '创建' || new_table_name || '失败');
                    continue;
                  end;
          /*
                  up_dm_st_mv_log(my_cur.tmp_origin_table,
                                  new_table_name,
                                  '',
                                  sysdate,
                                  sysdate,
                                  0,
                                  0,
                                  '创建' || new_table_name || '成功',
                                  '创建' || new_table_name || '成功');
                  */
              end;
        --20150512 modify by mqf 位置调整在这里
        acc_st.up_dm_st_mv_log(my_cur.tmp_origin_table,
                                  new_table_name,
                                  '',
                                  sysdate,
                                  sysdate,
                                  0,
                                  0,
                                  '创建' || new_table_name || '成功',
                                  '创建' || new_table_name || '成功');

              begin

                v_min_time := my_cur.tmp_max_squad_day;
                --v_max_time := my_cur.tmp_max_squad_day;

                insert into acc_st.st_idx_rpt_history
                  (table_name,
                   origin_table_name,
                   min_squad_day,
                   max_squad_day,
                   recd_count,
                   create_time)
                values
                  (new_table_name,
                   my_cur.tmp_origin_table,
                   v_min_time,
                   '',
                   0,
                   sysdate);

              end;

            end if;
          end;

        end;
      end loop;
    end;

  end;
  commit;
  out_msg       := '建表成功';
  out_retresult := 0;
exception
  when OTHERS THEN
    begin
      rollback;
      out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                       dbms_utility.format_error_backtrace() || ']行,' ||
                       new_table_name;
      out_retResult := sqlcode;
      return;
    end;

END up_dm_st_rpt_create1;
