CREATE OR REPLACE PROCEDURE ACC_ST.up_dm_st_rpt_create2(out_retResult OUT INTEGER, --返回结果
                                                        out_msg       OUT VARCHAR2 --返回信息
                                                        ) --输入的队列表名
  ---------------------------------------------------------------------------------
  --过程名：  up_dm_st_rpt_create2
  --功能描述：建立中间表的历史分表（有主、从表，日期存在主表中，数据在从表中）
  --输出参数  ：out_retResult 模块返回值 out_msg 返回信息
  --返回值    ：0：操作成功；其他：操作失败；out_retResult为0时，out_msg返回表名
  --创建者：  张建华
  --创建日期：20131021
  --修改日期：20130303
  --修改：将分表名中的序号扩展到6位
  --修改日期：20140517 modify by mqf
  -------------------------------------------------------------------------------
 AS

  v_msg           VARCHAR(50);
  v_min_time      date; --buf表中的最小交易时间
  v_max_time      date; --buf表中的最大交易时间
  v_new_date      varchar2(20);
  v_max_table     varchar2(50); --最大的历史表
  v_count         number(18);
  v_count2        number(18);
  v_flag          number(1); --创建000001的标志，0：创建
  v_time          varchar2(20);
  v_balance_time  varchar2(20);
  v_temp_sql      varchar2(8000); --临时sql语句
  v_table_sql     varchar2(5000); --建表临时sql语句
  v_temp_num      varchar2(6); --分表序列号
  v_limit_count   number(18); --各交易类型分表限制数量
  v_new_tablename varchar2(50);
  v_in_que        varchar2(50);
  v_time_type     varchar2(30);
  v_primarytable  VARCHAR2(50); --源表名对应的主表名
BEGIN
  --昨天清算的日期(用于建立第一个000001表)
  select to_char(sysdate - 1, 'yyyymmdd') into v_balance_time from dual;
  declare
    cursor scc_table is
      select t.origin_table_name tmp_origin_table, --包含用户前缀acc_st_his
             t.create_sql        tmp_create_sql,
             t.is_squadday       tmp_is_squadday,
             t.ab_name           tmp_ab_name,
             t.divide_recd_count tmp_divide_recd_count,
             t.date_type         tmp_date_type,
             t.primarytable      tmp_primarytable --主表表名
        from acc_st.st_cfg_clear_table t
       where t.db_type = 'rpt'
         and t.is_squadday = 0
         and t.primarytable is not null; --有主表
  begin
    for i_cur in scc_table loop
      --建001表
      --原始表没有数据，返回
      begin
        v_flag      := 2;
        v_table_sql := 'select count(*)  from ' || i_cur.tmp_origin_table;
        execute immediate v_table_sql
          into v_count;
        if 0 = v_count then
          continue;
        end if;
      end;

      v_time_type     := i_cur.tmp_date_type; --时间类型
      v_in_que        := i_cur.tmp_ab_name; --源表缩写
      v_table_sql     := i_cur.tmp_create_sql; --建表语句
      v_limit_count   := i_cur.tmp_divide_recd_count;
      v_primarytable  := i_cur.tmp_primarytable;
      v_new_tablename := v_in_que || '000001';

      begin
        begin
          select count(*)
            into v_count
            from acc_st.st_idx_rpt_history
           where table_name like '%' || v_in_que || '_%';
        end;
        begin
          v_temp_sql := 'select count(*) from dba_objects where object_name = upper (''' ||
                        v_new_tablename ||
                        ''') and owner=''ACC_ST_HIS'' and object_type =''TABLE''';
          execute immediate v_temp_sql
            into v_count2;
        end;
      end;
      if v_count = 0 then
        --st_idx_rpt_history没有交易分表的记录
        if v_count2 > 0 then
          --分表的物理表存在
          v_temp_sql := 'drop table acc_st_his.' || v_new_tablename;
          execute immediate v_temp_sql; --删除000001分表表
        end if;
        v_flag := 0;
      else
        ----st_idx_rpt_history有交易分表的记录
        if v_count2 = 0 then
          --分表的物理表不存在
          v_flag := 0;
        end if;
      end if;

      begin
        if v_flag = 0 then

          begin

            select REGEXP_REPLACE(v_table_sql, '%s', v_new_tablename)
              into v_temp_sql
              from dual;

            --每个建表语句中含有建索引、主键信息，且以；分割
            declare
              cursor sql_cur is
                select * from table(acc_st.up_dm_st_splitstr(v_temp_sql, ';'));
            begin
              for t_cur in sql_cur loop
                v_table_sql := t_cur.column_value;
                if trim(v_table_sql) is not null and v_table_sql <> ' ' then
                  execute immediate v_table_sql; --创建000001分表相关的操作
                end if;
              end loop;
            end;

            acc_st.up_dm_st_mv_log(v_in_que,
                            v_new_tablename,
                            '0000000000',
                            sysdate,
                            sysdate,
                            0,
                            0,
                            '成功创建' || v_new_tablename || '表',
                            'create table ' || v_new_tablename || '...');

          end;

          begin
            /**
            begin
              --第一次运行，取之前的所有的清算数据存放到第一张表
              v_temp_sql := 'select min(' || v_time_type || '),max(' ||
                            v_time_type || ') from ' || v_primarytable ||
                            ' where substr(balance_water_no,0,8)<= ''' ||
                            v_balance_time || '''';
              execute immediate v_temp_sql
                into v_min_time, v_max_time;
            end;**/
            begin
              --数据可能有延迟
              v_time     := '2013-05-01'; --to_char(v_min_time, 'yyyy-mm-dd');
              v_min_time := to_date(v_time || ' 00:00:00',
                                    'yyyy-mm-dd hh24:mi:ss');
            end;
            /**
            begin
              v_time     := to_char(v_max_time, 'yyyy-mm-dd');
              v_max_time := to_date(v_time || ' 23:59:59',
                                    'yyyy-mm-dd hh24:mi:ss');
            end;**/
            -- 初始化索引表中的0001表
            begin
              insert into acc_st.st_idx_rpt_history
                (table_name,
                 origin_table_name,
                 begin_date,
                 end_date,
                 recd_count,
                 create_time)
              values
                ('acc_st_his.' || v_new_tablename,
                 i_cur.tmp_origin_table,
                 v_min_time,
                 '',
                 0,
                 sysdate);

            end;
          end;

        end if;
      end;

      --建00000X表
      begin
        --取最大的历史表
        select max(table_name)
          into v_max_table
          from st_idx_rpt_history
         where origin_table_name = i_cur.tmp_origin_table;
      end;

      if v_max_table is null then
        continue;
      end if;

      begin
        v_temp_sql := 'select count(*)  from acc_st.st_idx_rpt_history  where table_name =''' ||
                      v_max_table || '''';
        execute immediate v_temp_sql
          into v_temp_num;
        v_temp_sql := 'select regexp_substr(table_name, ''.{1,6}$''), recd_count, to_char(end_date + 1, ''yyyy-mm-dd'')  from acc_st.st_idx_rpt_history     where table_name =''' ||
                      v_max_table || '''';
        execute immediate v_temp_sql
          into v_temp_num, v_count, v_new_date;
        /**
        select regexp_substr(table_name, '.{1, 4}$ '),
               recd_count,
               to_char(end_date + 1, ' yyyy - mm - dd ')
          into v_temp_num, v_count, v_new_date **/

        begin
          --下一分表的序列号
          v_temp_num := acc_st.up_dm_st_tableno(v_temp_num);
        end;
      end;

      begin
        if (v_count >= v_limit_count) then
          v_new_tablename := v_in_que || v_temp_num;

          select regexp_replace(v_table_sql, '%s', v_new_tablename)
            into v_temp_sql
            from dual;
          execute immediate v_temp_sql; --创建00000X分表

          v_min_time := to_date(v_new_date || ' 00:00:00',
                                'yyyy-mm-dd hh24:mi:ss');

          v_max_time := to_date(v_new_date || ' 23:59:59',
                                'yyyy-mm-dd hh24:mi:ss');
          acc_st.up_dm_st_mv_log(v_in_que,
                          v_new_tablename,
                          '0000000000',
                          sysdate,
                          sysdate,
                          0,
                          0,
                          ' 成功创建 ' || v_new_tablename || '表',
                          'create table  ' || v_new_tablename || '...');

          insert into acc_st.st_idx_rpt_history
            (table_name,
             origin_table_name,
             begin_date,
             end_date,
             recd_count,
             create_time)
          values
            ('acc_st_his.' || v_new_tablename,
             i_cur.tmp_origin_table,
             v_min_time,
             '',
             0,
             sysdate);

        end if;

      end;

    end loop;
  end;

  out_msg       := '建表成功';
  out_retresult := 0;
  commit;
exception
  when OTHERS THEN
    begin
      ROLLBACK;
      out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[ ' ||
                       dbms_utility.format_error_backtrace() || ']行 ';
      out_retresult := sqlcode;
      return;
    end;

END up_dm_st_rpt_create2;
/
