prompt
CREATE OR REPLACE PROCEDURE ACC_ST.up_dm_st_rpt_create3(out_retResult OUT INTEGER, --返回结果
                                                        out_msg       OUT VARCHAR2 --返回信息
                                                        )
---------------------------------------------------------------------------------
  --过程名：  up_dm_st_rpt_create3
  --功能描述：建立中间表的历史分表（按照清算流水号分表）
  --输出参数  ：out_retResult 模块返回值 out_msg 返回信息
  --返回值    ：0：操作成功；其他：操作失败；out_retResult为0时，out_msg返回表名
  --创建者：  张建华
  --创建日期：20131021
  --修改：将分表名中的序号扩展到6位
  --修改日期：20140517

  -------------------------------------------------------------------------------
 AS

  v_msg              VARCHAR(50);
  v_min_bal_water_no varchar2(10); --最小清算流水号
  --v_max_bal_water_no varchar2(10); --最大清算流水号
  v_new_bal_water_no varchar2(10);
  v_max_table        varchar2(30); --最大的历史表
  v_count            number(18);
  v_count2           number(18);
  v_flag             number(1); --创建001的标志，0：创建
  v_balance_time     varchar2(20);
  v_temp_sql         varchar2(8000); --临时sql语句
  v_table_sql        varchar2(5000); --建表临时sql语句
  v_temp_num         varchar2(6); --分表序列号
  v_limit_count      number(18); --各交易类型分表限制数量
  v_new_tablename    varchar2(30);
  v_in_que           varchar2(30);
  v_time_type        varchar2(30);
  v_owner            varchar2(11);
BEGIN
  --昨天清算的日期(用于建立第一个0001表)
  select to_char(sysdate - 1, 'yyyymmdd') into v_balance_time from dual;
  declare
    cursor scc_table is
      select t.origin_table_name tmp_origin_table, --包含用户前缀acc_st或acc_st_his
             t.create_sql        tmp_create_sql,
             t.is_squadday       tmp_is_squadday,
             t.ab_name           tmp_ab_name,
             t.divide_recd_count tmp_divide_recd_count,
             t.date_type         tmp_date_type,
             t.primarytable      tmp_primarytable --主表表名
        from acc_st.st_cfg_clear_table t
       where t.db_type = 'rpt'
         and t.is_squadday = 0
         and t.primarytable is null;
  begin
    for i_cur in scc_table loop
      --建0001表
      --原始表没有数据，返回
      begin
        v_flag      := 2;
        v_table_sql := 'select count(*)  from ' || i_cur.tmp_origin_table;
        execute immediate v_table_sql
          into v_count;
        if 0 = v_count then
          continue;
        end if;
      end;

      v_time_type     := i_cur.tmp_date_type; --时间类型
      v_in_que        := i_cur.tmp_ab_name; --源表缩写
      v_table_sql     := i_cur.tmp_create_sql; --建表语句
      v_limit_count   := i_cur.tmp_divide_recd_count;
      v_new_tablename := v_in_que || '000001';
      v_owner         := substr(i_cur.tmp_origin_table,
                                1,
                                INSTR(i_cur.tmp_origin_table, '.', 1, 1) - 1);
      begin
        begin
          select count(*)
            into v_count
            from acc_st.st_idx_rpt_history
           where table_name like '%' || v_in_que || '_%';
        end;
        begin
          v_temp_sql := 'select count(*) from dba_objects where object_name = upper (''' ||
                        v_new_tablename || ''')  and owner= upper (''' ||
                        v_owner || ''') and object_type =''TABLE''';
          execute immediate v_temp_sql
            into v_count2;
        end;
      end;
      if v_count = 0 then
        --st_idx_rpt_history没有交易分表的记录
        if v_count2 > 0 then
          if substr(v_in_que, 1, 11) = 'acc_st_his' then
            v_new_tablename := 'acc_st_his.';
          end if;
          --分表的物理表存在
          v_temp_sql := 'drop table ' || v_owner || '.' || v_new_tablename; --20150428 modify by mqf 增加v_owner
          execute immediate v_temp_sql; --删除001分表表
        end if;
        v_flag := 0;
      else
        ----st_idx_rpt_history有交易分表的记录
        if v_count2 = 0 then
          --分表的物理表不存在
          v_flag := 0;
        end if;
      end if;
      begin
        if v_flag = 0 then
          --建0001表
          begin
            --获取建表语句
            --替换表名
            select regexp_replace(v_table_sql, '%s', v_new_tablename)
              into v_temp_sql
              from dual;

            --每个建表语句中含有建索引、主键信息，且以；分割
            declare
              cursor sql_cur is
                select * from table(acc_st.up_dm_st_splitstr(v_temp_sql, ';'));
            begin
              for t_cur in sql_cur loop
                v_table_sql := t_cur.column_value;
                if trim(v_table_sql) is not null and v_table_sql <> ' ' then
                  execute immediate v_table_sql; --创建0001分表相关的操作
                end if;
              end loop;
            end;

      --20150512 add by mqf 增加日志
      acc_st.up_dm_st_mv_log(v_in_que,
                          v_new_tablename,
                          '0000000000',
                          sysdate,
                          sysdate,
                          0,
                          0,
                          '成功创建' || v_new_tablename || '表',
                          'create table ' || v_new_tablename || '...');

          end;
          /*
          v_table_sql := 'select min(balance_water_no)  from ' ||
                         i_cur.tmp_origin_table;
          execute immediate v_table_sql
            into v_min_bal_water_no;
      */
        v_min_bal_water_no := '2013050101'; --20150428 modify by mqf --数据可能有延迟，初始最小清算流水号改为2013050101

          begin
            insert into acc_st.st_idx_rpt_history
              (table_name,
               origin_table_name,
               begin_balance_no,
               end_balance_no,
               recd_count,
               create_time)
            values
              (v_new_tablename,
               i_cur.tmp_origin_table,
               v_min_bal_water_no,
               '',
               0,
               sysdate);

          end;

        end if;
      end;

      --建000X表
      begin
        --取最大的历史表
        select max(table_name)
          into v_max_table
          from acc_st.st_idx_rpt_history
         where origin_table_name = i_cur.tmp_origin_table;
      end;

      if v_max_table is null then
        continue;
      end if;

      begin
        select regexp_substr(v_max_table, '.{1,6}$'),
               recd_count,
               to_char(to_date(substr(end_balance_no, 1, 8), 'yyyymmdd') + 1,
                       'yyyymmdd')
          into v_temp_num, v_count, v_new_bal_water_no
          from acc_st.st_idx_rpt_history
         where table_name = v_max_table;

        begin
          --下一分表的序列号
          v_temp_num := acc_st.up_dm_st_tableno(v_temp_num);
        end;
      end;

      begin
        if (v_count >= v_limit_count) then
          v_new_tablename := v_in_que || v_temp_num;
          begin
            select regexp_replace(v_table_sql, '%s', v_new_tablename)
              into v_temp_sql
              from dual;

            --每个建表语句中含有建索引、主键信息，且以；分割
            declare
              cursor sql_cur is
                select * from table(up_dm_st_splitstr(v_temp_sql, ';'));
            begin
              for t_cur in sql_cur loop
                v_table_sql := t_cur.column_value;
                if trim(v_table_sql) is not null and v_table_sql <> ' ' then
                  execute immediate v_table_sql; --创建000X分表相关的操作
                end if;
              end loop;
            end;
          end;

          v_min_bal_water_no := v_new_bal_water_no || '01';

          --v_max_bal_water_no := v_new_bal_water_no;
          acc_st.up_dm_st_mv_log(v_in_que,
                          v_new_tablename,
                          '0000000000',
                          sysdate,
                          sysdate,
                          0,
                          0,
                          '成功创建' || v_new_tablename || '表',
                          'create table ' || v_new_tablename || '...');

          insert into acc_st.st_idx_rpt_history
            (table_name,
             origin_table_name,
             begin_balance_no,
             end_balance_no,
             recd_count,
             create_time)
          values
            (v_new_tablename,
             i_cur.tmp_origin_table,
             v_min_bal_water_no,
             '',
             0,
             sysdate);

        end if;

      end;

    end loop;
  end;
  commit;
  out_msg       := v_new_tablename;
  out_retresult := 0;

exception
  when OTHERS THEN
    begin
      ROLLBACK;
      out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                       dbms_utility.format_error_backtrace() || ']行';
      out_retresult := sqlcode;
      return;
    end;

END up_dm_st_rpt_create3;
/
grant execute on ACC_ST.UP_DM_ST_RPT_CREATE3 to ACC_ST_APP;


