prompt
CREATE OR REPLACE PROCEDURE ACC_ST.up_dm_st_rpt_mv1(out_retresult OUT INTEGER, --返回结果
                                             out_msg       OUT VARCHAR2 --返回信息
                                             )
---------------------------------------------------------------------------------
  --过程名：  up_dm_st_rpt_mv1
  --功能描述：中央清算--中间表数据根据运营日squad_day导入历史表，动态插入
  --输入参数: @origin_table_name  中间表名
  --输出参数  ：return_num 模块返回值
  --返回值    ：0：操作成功；其他：操作失败；
  --创建者：张建华
  --创建日期：20131029
  --创建描述：当目前所使用的表中记录保存时间超过预先设定的保存天数时，
  --          则将本表中超出保存时间范围的记录值插入历史分表中进行保存后再进行删除操作，
  --          所有操作均需有相应日志保存于清理日志表clear_table_log中
  --修改日期： 20150424 by mqf
  --修改内容： 按运营日期遍历，记录数大于分表最大记录数则停止迁移数据，新建分表再迁移数据。
             --解决上线运行时，堆积大量的记录插入到第一个分表的问题
  --修改日期：20160912 modify by mqf
  --修改内容：1.关闭游标

  -------------------------------------------------------------------------------
 AS
  v_sql               varchar2(2048);
  v_max_table         varchar2(50); --最大的历史表
  v_min_table         varchar2(50); --最小的历史表
  v_count             int; --
  v_begin_time        date; --清理开始时间
  v_mindate           varchar2(8); --最小运营日
  v_maxdate           varchar2(8); --最大运营日
  v_origin_table_name varchar2(50); --中间表表名
  v_keep_days         int; --中间表保存天数
  v_table_columns     varchar2(1000); --中间表各字段
  v_cost_seconds      int; --统计时间
  v_msg               varchar2(255); --注释
  v_balance_water_no  varchar2(10); --注释
  --v_max_blane_water_no varchar2(10);

  --20140424 add by mqf
  v_tmp1              varchar2(256);
  v_recd_count        integer; --索引表已更新记录数
  divide_recd_count   integer; --配置表最大记录数
  v_rowcount          integer; --更新记录数
  v_squad_day_value   varchar2(8); --日期
  TYPE MYCURSOR IS REF CURSOR;  --定义游标类型
  cur_date_type MYCURSOR;       --定义游标
BEGIN

  -------------------------迁移数据开始---------------------------
  select max(balance_water_no)
    into v_balance_water_no
    from acc_st.st_list_balance_waiting;
  begin
    declare
      cursor scc_table is
        select t.origin_table_name tmp_origin_table,
               t.keep_days         tmp_keep_days,
               t.clear_flag        tmp_clear_flag,
               t.is_squadDay       tmp_is_squadDay,
               t.table_columns     tmp_table_columns，
         t.divide_recd_count tmp_divide_recd_count
          from acc_st.st_cfg_clear_table t
         where --t.clear_flag = 1 and
         t.is_squadday = 1
         and t.db_type = 'rpt'; --
    begin

      for i_cur in scc_table loop
        begin
          v_origin_table_name := trim(i_cur.tmp_origin_table);  --包含用户前缀acc_st
          v_table_columns     := trim(i_cur.tmp_table_columns);
          v_begin_time        := sysdate;
          v_keep_days         := i_cur.tmp_keep_days;
      --20150424 add by mqf
        divide_recd_count := trim(i_cur.tmp_divide_recd_count);

          -- select min_squadDate into minDate from t#st_sts_inc_day_min;
          --delete from t#st_sts_inc_day_min;
          v_sql := ' select max(table_name) ,min(table_name)  from acc_st.st_idx_rpt_history where origin_table_name =''' ||
                   v_origin_table_name || '''';
          execute immediate v_sql
            into v_max_table, v_min_table;
          if v_max_table is null or v_min_table is null then
            continue;
          end if;
          --循环向分表中插入数据
          begin
            declare
              cursor index_table is
                select table_name    temp_table_name,
                       min_squad_day temp_min_squad_day,
                       max_squad_day temp_max_squad_day
                  from acc_st.st_idx_rpt_history t
                 where origin_table_name = v_origin_table_name
                   and table_name >= v_min_table
                   and table_name < v_max_table;

            begin
              for t_cur in index_table loop

                -- 根据运营日插入对应的历史表
                v_sql := ' select count(*) from ' || v_origin_table_name ||
                         ' where squad_day >=''' ||
                         t_cur.temp_min_squad_day ||
                         ''' and squad_day <=''' ||
                         t_cur.temp_max_squad_day || '''';
                execute immediate v_sql
                  into v_count;
                if v_count = 0 then
                  out_msg := '没有数据需要迁移';
                  continue;
                end if;
        v_begin_time := sysdate; --20150512 add by mqf
                begin
                  v_sql := ' insert into acc_st.' || t_cur.temp_table_name ||
                           ' select ' || v_table_columns || ' from ' ||
                           v_origin_table_name || ' where squad_day >=''' ||
                           t_cur.temp_min_squad_day ||
                           ''' and squad_day <=''' ||
                           t_cur.temp_max_squad_day || '''';
                  execute immediate v_sql;
                EXCEPTION
                  when others then
                    begin
                      rollback;
                      out_retresult  := SQLCODE;
                      out_msg        := v_origin_table_name ||
                                        '插入历史表错误,错误代码:' || SQLERRM ||
                                        ',发生在第[' ||
                                        dbms_utility.format_error_backtrace() || ']行';
                      v_cost_seconds := round(to_number(sysdate -
                                                        v_begin_time) * 24 * 60 * 60);
                      acc_st.up_dm_st_mv_log(v_origin_table_name,
                                      '0',
                                      v_balance_water_no,
                                      v_begin_time,
                                      sysdate,
                                      v_cost_seconds,
                                      0,
                                      out_msg,
                                      v_sql);
                      continue;
                    end;

                end;
        --20150512 add by mqf 增加插入数据日志
        v_cost_seconds := ROUND(TO_NUMBER(sysdate - v_begin_time) * 24 * 60 * 60);
          out_msg        := '从' || v_origin_table_name || '表插入数据到' ||
                  t_cur.temp_table_name || '，共' || v_count || '条';
          acc_st.up_dm_st_mv_log(v_origin_table_name,
                  t_cur.temp_table_name,
                  v_balance_water_no,
                  v_begin_time,
                  sysdate,
                  v_cost_seconds,
                  v_count,
                  out_msg,
                  v_sql);

                update acc_st.st_idx_rpt_history
                   set recd_count = recd_count + v_count
                 where table_name = t_cur.temp_table_name;
                v_sql := 'delete from ' || v_origin_table_name ||
                         ' where squad_day >=''' ||
                         t_cur.temp_min_squad_day ||
                         ''' and squad_day <=''' ||
                         t_cur.temp_max_squad_day || '''';
                execute immediate v_sql;

        v_begin_time := sysdate; --20150520 add by mqf
        out_msg := '从' || v_origin_table_name || '删除' || v_count || '条数据'; --20150520 modify by mqf
        v_cost_seconds := round(to_number(sysdate - v_begin_time) * 24 * 60 * 60);
        acc_st.up_dm_st_mv_log(v_origin_table_name,
                '0',
                v_balance_water_no,
                v_begin_time,
                sysdate,
                v_cost_seconds,
                v_count,
                out_msg,
                v_sql);

              end loop;
              commit;
            end;
          end;

          begin
            --最大交易日期
            v_maxdate := to_char(sysdate - v_keep_days, 'yyyymmdd');
            begin

              --最小交易日期
        --20150424 modify by mqf 增加查询recd_count
              v_sql := ' select min_squad_day,recd_count   from acc_st.st_idx_rpt_history where table_name =''' ||
                       v_max_table || '''';

              execute immediate v_sql
                into v_mindate, v_recd_count;

            exception
              when OTHERS THEN
                begin
                  v_msg := '查询最小交易日期报错：' || v_sql;
                  acc_st.up_dm_st_mv_log(v_origin_table_name,
                                  v_max_table,
                                  v_balance_water_no,
                                  sysdate,
                                  sysdate,
                                  0,
                                  v_count,
                                  v_msg,
                                  v_sql);
                  continue;
                end;
            end;
      --索引表已迁移记录大于或等于分表最大记录数，将跳到一次循环 20150520 modify by mqf
      if v_recd_count>=divide_recd_count then
          continue;
      end if;

            v_sql := ' select count(*) from ' || v_origin_table_name ||
                     ' where squad_day >=''' || v_mindate ||
                     ''' and squad_day <=''' || v_maxdate || '''';
            execute immediate v_sql
              into v_count;

          end;

          if v_count <= 0 then
            continue;
          end if;
          /**
          --获取最大交易日期的清算流水号
          v_sql := 'select max(balance_water_no) from ' ||
                   v_origin_table_name || ' where squad_day <=''' ||
                   v_maxdate || '''';
          execute immediate v_sql
            into v_max_blane_water_no;**/

          --20150426 add by mqf
      v_tmp1 := 'lock table acc_st.' || v_max_table || ' in exclusive mode';
          execute immediate v_tmp1;

      v_begin_time := sysdate; --20150512 add by mqf
      --20150426 add by mqf 按运营日期遍历，解决上线运行时，堆积大量的记录插入到第一个分表中。
      v_sql := ' select distinct squad_day from ' || v_origin_table_name || ' where squad_day >=''' ||
                   v_mindate || ''' and squad_day <=''' || v_maxdate || ''' order by squad_day ';
        --更新记录数初始为0
        v_rowcount := 0;

      open cur_date_type for v_sql;
        loop
        fetch cur_date_type into v_squad_day_value;
          exit when cur_date_type%notfound;

        --超过分表最大记录数，将结束循环
        if (v_recd_count+v_rowcount>=divide_recd_count) then
          exit;
        end if;

        v_maxdate := v_squad_day_value;
        v_sql := ' insert into acc_st.' || v_max_table || '(' || v_table_columns ||
             ') select ' || v_table_columns || ' from ' ||
             --v_origin_table_name || ' where squad_day >=''' ||
             --v_mindate || ''' and squad_day <=''' || v_maxdate || '''';
             v_origin_table_name || ' where squad_day =''' ||
             v_maxdate || '''';
        execute immediate v_sql;
        --累加更新记录数
        v_rowcount := v_rowcount + sql%rowcount;
      end loop;
    close cur_date_type; --20160912 add by mqf 关闭游标

      --20150424 add by mqf 更新v_count
        v_count := v_rowcount;

      --20150512 add by mqf 增加插入数据日志
      v_cost_seconds := round(to_number(sysdate - v_begin_time) * 24 * 60 * 60);
      out_msg        := '从' || v_origin_table_name || '表插入数据到' || v_max_table || '，共' ||
              v_count || '条';

      acc_st.up_dm_st_mv_log(v_origin_table_name,
              v_max_table,
              v_balance_water_no,
              v_begin_time,
              sysdate,
              v_cost_seconds,
              v_count,
              out_msg,
              v_sql);
      --增加插入数据日志

          --再更新最大历史表的最大运营日期为昨天
      if v_count >0 then --20150512 add by mqf 记录大于0才更新索引表、删除源表数据
        update acc_st.st_idx_rpt_history t
         set max_squad_day = v_maxdate,
           -- t.end_balance_no = v_max_blane_water_no,
           t.recd_count = t.recd_count + v_count
         where table_name = v_max_table;


        -------------------------删除数据开始---------------------------
        begin
        v_begin_time := sysdate; --20150512 add by mqf
        --删除配置表中此中间表保存天数前的数据
        begin
          v_sql := 'delete from ' || v_origin_table_name ||
               ' where squad_day >=''' || v_mindate ||
               ''' and squad_day <=''' || v_maxdate || '''';
          execute immediate v_sql;
        end;
        --记录日志
        --out_msg        := '删除' || v_origin_table_name || '表中清理天数前的数据成功';
        out_msg := '从' || v_origin_table_name || '删除' || v_count || '条数据'; --20150512 modify by mqf
        v_cost_seconds := round(to_number(sysdate - v_begin_time) * 24 * 60 * 60);
        acc_st.up_dm_st_mv_log(v_origin_table_name,
                '0', --20150520 modify by mqf v_max_table改为'0'
                v_balance_water_no,
