prompt Creating procedure UP_DM_ST_TK_SYN
prompt ==================================
prompt
CREATE OR REPLACE PROCEDURE ACC_ST.up_dm_st_tk_syn(out_retResult OUT INTEGER, --返回结果
                                            out_msg       OUT VARCHAR2) --输入的队列表名
  ---------------------------------------------------------------------------------
  --过程名：  up_dm_st_tk_syn
  --功能描述：运营管理系统和票库系统之间数据同步
  --输出参数  ：out_retResult 模块返回值 out_msg 返回信息
  --返回值    ：0：操作成功；其他：操作失败；out_retResult为0时，out_msg返回表名
  --创建者：  张建华
  --创建日期：201301109
  --修改：20150525 modify by mqf 增加值、字段 sysdate INSERT_DATE
  -------------------------------------------------------------------------------
 AS

  v_msg          VARCHAR(50);
  v_sql          varchar2(1000);
  --v_date         date; --
  v_keep_days    int;
  v_count        int;
  v_column_names varchar2(500);

  v_date_str     VARCHAR2(20);
BEGIN
  out_msg       := '数据同步成功';
  out_retresult := sqlcode;
  declare
    cursor scc_table is
      select t.src_table      tmp_src_table,
             t.src_db         tmp_src_db,
             t.dest_table     tmp_dest_table,
             t.dest_db        tmp_dest_db,
             t.dest_table_bak tmp_dest_table_bak,
             t.keep_days      tmp_keep_days,
             t.column_names   tmp_column_names
        from acc_st.op_dm_syn_config t;
  begin
    for i_cur in scc_table loop
      v_keep_days    := i_cur.tmp_keep_days;
      --v_date         := sysdate - v_keep_days;
    v_date_str := to_char(sysdate - v_keep_days,'yyyy-mm-dd hh24:mi:ss');
      v_column_names := i_cur.tmp_column_names;
      --
      v_sql := 'select count(*) from ' || i_cur.tmp_dest_db || '.' ||
               --i_cur.tmp_dest_table_bak || ' where insert_date<''' ||
               --v_date || ''' ';
         i_cur.tmp_dest_table_bak || ' where insert_date < to_date(''' || --20150525 modify by mqf
         v_date_str ||''', ''yyyy-mm-dd hh24:mi:ss'')';
      execute immediate v_sql
        into v_count;
      --删除有效期前的备份数据。
      begin
        v_sql := 'delete from ' || i_cur.tmp_dest_db || '.' ||
                 --i_cur.tmp_dest_table_bak || ' where insert_date<''' ||
                 --v_date || '''';
         i_cur.tmp_dest_table_bak || ' where insert_date< to_date(''' || --20150525 modify by mqf
         v_date_str ||''', ''yyyy-mm-dd hh24:mi:ss'')';
        execute immediate v_sql;
      EXCEPTION
        when others then
          begin
            rollback;
            out_retresult := SQLCODE;
            out_msg       := '删除有效期前的备份数据' || i_cur.tmp_dest_table_bak ||
                             '出错：错误代码：' || SQLERRM || ',发生在第[' ||
                             dbms_utility.format_error_backtrace() || ']行';

            acc_st.up_dm_st_tk_syn_log(i_cur.tmp_dest_db,
                                i_cur.tmp_dest_table_bak,
                                '',
                                '',
                                1,
                                0,
                                out_msg);
            continue;
          end;
      end;
      out_msg := '成功删除' || v_count || '条数据从' || i_cur.tmp_dest_db || '.' ||
                 i_cur.tmp_dest_table_bak;
      acc_st.up_dm_st_tk_syn_log(i_cur.tmp_dest_db,
                          i_cur.tmp_dest_table_bak,
                          '',
                          '',
                          0,
                          v_count,
                          out_msg);
      --备份数据
      begin
        begin
          v_sql := 'insert into   ' || i_cur.tmp_dest_db || '.' ||
                   i_cur.tmp_dest_table_bak || '(' ||
                   i_cur.tmp_column_names || ',INSERT_DATE) select ' ||
                   i_cur.tmp_column_names || ' ,sysdate INSERT_DATE from ' || i_cur.tmp_dest_db || '.' || --20150525 modify by mqf 增加值、字段 sysdate INSERT_DATE
                   i_cur.tmp_dest_table;
          execute immediate v_sql;
        EXCEPTION
          when others then
            begin
              rollback;
              out_retresult := SQLCODE;
              out_msg       := '备份数据到' || i_cur.tmp_dest_db || '.' ||
                               i_cur.tmp_dest_table_bak || '表数据报错：错误代码：' ||
                               SQLERRM || ',发生在第[' ||
                               dbms_utility.format_error_backtrace() || ']行';

              acc_st.up_dm_st_tk_syn_log(i_cur.tmp_dest_db,
                                  i_cur.tmp_dest_table_bak,
                                  '',
                                  '',
                                  1,
                                  0,
                                  out_msg);
              continue;
            end;
        end;

        v_sql := ' select count(*) from ' || i_cur.tmp_dest_db || '.' ||
                 i_cur.tmp_dest_table;
        execute immediate v_sql
          into v_count;
        out_msg := '成功备份' || v_count || '条数据到' || i_cur.tmp_dest_db || '.' ||
                   i_cur.tmp_dest_table_bak;
        acc_st.up_dm_st_tk_syn_log(i_cur.tmp_dest_db,
                            i_cur.tmp_dest_table,
                            i_cur.tmp_dest_db,
                            i_cur.tmp_dest_table_bak,
                            0,
                            v_count,
                            out_msg);
      end;
      --删除目的表数据
      begin
        v_sql := 'delete from ' || i_cur.tmp_dest_db || '.' ||
                 i_cur.tmp_dest_table;
        execute immediate v_sql;
      end;

      begin
        begin
          --同步数据
          v_sql := 'insert into   ' || i_cur.tmp_dest_db || '.' ||
                   i_cur.tmp_dest_table || ' select ' || v_column_names ||
                   ' from ' || i_cur.tmp_src_db || '.' ||
                   i_cur.tmp_src_table || ' where record_flag=0';
          execute immediate v_sql;
        EXCEPTION
          when others then
            begin
              rollback;
              out_retresult := SQLCODE;
              out_msg       := '同步数据到' || i_cur.tmp_dest_db || '.' ||
                               i_cur.tmp_dest_table || '表数据报错：错误代码：' ||
                               SQLERRM || ',发生在第[' ||
                               dbms_utility.format_error_backtrace() || ']行';

              acc_st.up_dm_st_tk_syn_log(i_cur.tmp_src_db,
                                  i_cur.tmp_src_table,
                                  i_cur.tmp_dest_db,
                                  i_cur.tmp_dest_table,
                                  1,
                                  0,
                                  out_msg);
              continue;
            end;
        end;
        --同步数据
        v_sql := 'select count(*) from ' || i_cur.tmp_src_db || '.' ||
                 i_cur.tmp_src_table || ' where record_flag=0';
        execute immediate v_sql
          into v_count;
        out_msg := '成功同步' || v_count || '条数据到' || i_cur.tmp_dest_db || '.' ||
                   i_cur.tmp_dest_table;
        acc_st.up_dm_st_tk_syn_log(i_cur.tmp_src_db,
                            i_cur.tmp_src_table,
                            i_cur.tmp_dest_db,
                            i_cur.tmp_dest_table,
                            0,
                            v_count,
                            out_msg);
      end;
    end loop;
  end;
  commit;
exception
  when OTHERS THEN
    begin
      ROLLBACK;
      out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                       dbms_utility.format_error_backtrace() || ']行';
      out_retresult := sqlcode;
      return;
    end;

END up_dm_st_tk_syn;
/
grant execute on ACC_ST.UP_DM_ST_TK_SYN to ACC_ST_APP;


