                                 ||' and b.dev_type_id = ''' || in_dev_type_id || ''' ) '
                                 
                                 ||' union all '
                                 
                                 ||' select line_id,station_id,dev_type_id,device_id,parm_type_id,record_flag,version_no,report_date,remark, ''0'',' 
