--------------------------------------------------------------------------------------
aS
v_squadDay varchar(8);
v_balanceday varchar(8);
    BEGIN
v_squadDay:=substr(in_squad_day,1,8);
v_balanceday:=substr(in_balance_day,1,8);
 /* insert into  t#op_err_card_result(
