      insert into t#OP_INC_STA_DISCOUNT_rpt
      (squad_day,line_id,card_main_id,card_sub_id,income,deal_num)
      select squad_day,line_id,card_main_id,
      case when card_main_id='06' then '00' else card_sub_id end,
