---------------------------------------------------------------------------------
  --过程名：  UP_OP_MB_STA_ACC_LCC_DAY
  --功能描述：手机支付报表--ACC与手机支付对账日报
  --输出参数  : 无
  --创建者： mqf
  --创建日期：20160112
  --报表编号：rpt_10_003
  --临时表：T#OP_MB_STA_ACC_LCC_DAY_RESULT
  -------------------------------------------------------------------------------
  --修改者：mqf
  --修改日期：20160823
  --修改内容：总计差异数（支付平台-ACC)由累计差异数（支付平台-ACC)，改为总计差异数（支付平台-ACC) = 总计支付平台 - 总计ACC
  -------------------------------------------------------------------------------
 is
  v_balance_begin_day char(8);
  v_balance_cur_day char(8);
  v_i      integer;
  v_j      integer;
BEGIN
  v_balance_cur_day := substr(in_balance_day,1,8);
  v_balance_begin_day := substr(in_balance_day,1,6) || '01';
-----------------------------------------------------------------------------------
v_i := 1;
while v_i <= substr(in_balance_day,7,2) loop
    v_j := 1;
    while v_j <= 3 loop
        insert into acc_st.T#OP_MB_STA_ACC_LCC_DAY_RESULT
        values
        (lpad(v_i,2,0),
             to_char(v_j),
             null,
             0,
             0,
             0,
             0,
             0,
             0,
             0,
             0);
        v_j := v_j + 1;
    end loop;
    v_i := v_i + 1;
  end loop;
---------------------------------------------------------------------------------
----手机支付LCC对账表 发售金额 即 发售押金
  insert into acc_st.T#OP_MB_STA_ACC_LCC_DAY_RESULT (
     day,
     data_type,
     data_type_name,
     sale_num,
     deposit_fee,
     deal_num,
     deal_fee,
     return_num,
     return_fee,
     lock_num,
     unlock_num)
    select lpad(substr(balance_water_no, 7, 2),2,0),
           '1',
           null,
           sum(nvl(sale_num,0)),
           sum(nvl(sale_fee,0)),
           sum(nvl(deal_num,0)),
           sum(nvl(deal_fee,0)),
           sum(nvl(return_num,0)),
           sum(nvl(return_fee,0)),
           sum(nvl(lock_num,0)),
           sum(nvl(unlock_num,0))
      from acc_st.st_mb_sts_lcc
