---------------------------------------------------------------------------------
--对应模板名：  rpt_05_008
--功能描述：票卡区段日报
--修改者：  王可佳
--修改日期：20130810
-------------------------------------------------------------------------------
--添加线路条件查询：20160318  by lindaquan

in_line_id char,
in_station_id char,
in_squad_day char,
in_balance_day char,
in_card_main_id char,
in_card_sub_id char,
out_cursor  OUT sys_refcursor)
AS
       v_balanceday char(8);
       v_squadday char(8);
       v_retval int;
       v_type_name varchar2(50);--大线路名称（轨道线、磁浮线）
    BEGIN
       v_balanceday:=substr(in_balance_day,1,8);
       v_squadday:=substr(in_squad_day,1,8);
       select distinct description into v_type_name from acc_st.op_cod_line_type where type_id= in_line_id;

--insert into t#op_fare_zone_rpt4 values ('01','00','普通成人单程票');
--insert into t#op_fare_zone_rpt4 values ('02','00','成人普通储值票');
--insert into t#op_fare_zone_rpt4 values ('02','01','学生普通储值票');
--insert into t#op_fare_zone_rpt4 values ('02','02','老人免费储值票');
--insert into t#op_fare_zone_rpt4 values ('02','03','老人优惠储值票');
--insert into t#op_fare_zone_rpt4 values ('03','00','普通乘次票');
--insert into t#op_fare_zone_rpt4 values ('04','00','普通纪念票');
--insert into t#op_fare_zone_rpt4 values ('04','01','优惠纪念票');
--insert into t#op_fare_zone_rpt4 values ('06','00','城市通票');
   insert into t#op_fare_zone_rpt4
     select card_main_id,card_sub_id,card_sub_name
       from op_prm_card_sub where record_flag='0' and card_main_id!='06';
   insert into t#op_fare_zone_rpt4 values ('06','00','城市通票');
   /*
   insert into t#op_fare_zone_rpt3 values('区段'||'1');
   insert into t#op_fare_zone_rpt3 values('区段'||'2');
   insert into t#op_fare_zone_rpt3 values('区段'||'3');
   insert into t#op_fare_zone_rpt3 values('区段'||'4');
   insert into t#op_fare_zone_rpt3 values('区段'||'5');
   */
