in_station_id char,
in_squad_day char,
in_balance_day char,
in_card_main_id char,
in_card_sub_id char,
out_cursor  OUT sys_refcursor)
AS
V_balanceday char(8);
V_squadday char(8);
v_type_name varchar2(50);
BEGIN
 v_balanceday:=substr(in_balance_day,1,8);
