-------------------------------------------------------------------------------

in_line_id char,
in_station_id char,
in_squad_day char,
in_balance_day char,
in_card_main_id char,
in_card_sub_id char,
out_cursor  OUT sys_refcursor)
AS
   v_balanceyear   char(4);--清算年
