   select card_main_id,case when card_main_id='06' then ' ' else card_sub_id end as card_sub_id,squad_day,sum(nvl(distance_num,0)),sum(nvl(total_distance,0))
   from ST_STS_FLW_AVERAGE_DISTANCE
