           ' and BEGIN_LINE in(select line_id from acc_st.op_cod_line_type where type_id= ''' ||in_line_id || ''')' ||
           ' group by card_main_id,card_sub_id,squad_day';
      execute immediate v_sql;

    end loop;

  end;
  end;
