   where a.card_main_code=b.card_main_code
   and a.card_sub_code=b.card_sub_code
   group by a.squad_day,a.card_main_code,a.card_sub_code, b.card_sub_desc;
  --------------------------------------------尾列 每日合计
   insert into  t#op_ave_dist_result
