  --取报表流水的最小流水号
   open poRs for select min(BALANCE_WATER_NO) BALANCE_WATER_NO from st_sys_flow_report
                                                               where finish_flag='0';
   commit;
   exception
     when others then
   begin
    poRetMsg :='错误代码：'||sqlcode||' 错误消息：'||sqlerrm;
   end;
end;
