  ELSE
     TMP_D_LINE_ID := V_D_LINE_ID;
  end if;

  if V_D_STATION_ID IS NULL then
     TMP_D_STATION_ID := '';
  ELSE
     TMP_D_STATION_ID := V_D_STATION_ID;
  end if;

  -- 清理临时表
  delete T#SR_MIN_AVG_DISTANCE;

  -- 比例阀值
