----------------------------------------------------
insert into T#ST_UP_ST_EXP_AUS(FILE_STATUS,TOTAL_RECORD_NUM_AUS,TOTAL_RECORD_FEE_AUS,TOTAL_RECORD_NUM_AUF,TOTAL_RECORD_FEE_AUF,
