update T#ST_UP_ST_EXP_AUS set FILE_STATUS='00'
                          where TOTAL_RECORD_NUM_AUS=TOTAL_RECORD_NUM_AUF AND TOTAL_RECORD_FEE_AUS=TOTAL_RECORD_FEE_AUF;
