prompt
CREATE OR REPLACE PROCEDURE ACC_ST.up_st_ist_settle_rptsquaday_mv(out_retresult     OUT INTEGER, --返回结果
                                                           out_msg           OUT VARCHAR2, --返回信息
                                                           oper_no           in VARCHAR2, --操作员信息
                                                           center_balance_no in varchar2) --输入的清算流水号
  ---------------------------------------------------------------------------------
  --过程名：  UP_balance_his_mid
  --功能描述：中央清算--中间表数据根据运营日squad_day导入历史表，动态插入
  --输入参数: @origin_table_name  中间表名
  --输出参数  ：return_num 模块返回值
  --返回值    ：0：操作成功；其他：操作失败；
  --创建者：张建华
  --创建日期：20131029
  --创建描述：当目前所使用的表中记录保存时间超过预先设定的保存天数时，
  --          则将本表中超出保存时间范围的记录值插入历史分表中进行保存后再进行删除操作，
  --          所有操作均需有相应日志保存于清理日志表clear_table_log中
  -------------------------------------------------------------------------------
 AS
  --memo      varchar2(255); --注释
  --err       int; --错误信息
  v_sql     varchar2(2048);
  max_table varchar2(40); --最大的历史表
  min_table varchar2(40); --最小的历史表
  v_count   integer; --
  --ss        varchar2(2048);

  begin_time date; --清理开始时间
  minDate    varchar2(8); --最小运营日
  --maxDate      varchar2(8); --最大运营日
  --max_water_no varchar2(10); --导入数据后的历史表最大流水号

  origin_table_name varchar2(30); --中间表表名
  keep_days         int; --中间表保存天数
  --clear_flag        int; --是否清理标志   1 清理，2，不清理
  --is_squadDay       int; --是否有清算运营日字段 1 有，0 没有
  table_columns  varchar2(1000); --中间表各字段
  v_cost_seconds int; --统计时间
  v_msg          varchar2(255); --注释
BEGIN

  -------------------------迁移数据开始---------------------------
  begin
    declare
      cursor scc_table is
        select t.origin_table_name tmp_origin_table,
               t.keep_days         tmp_keep_days,
               t.clear_flag        tmp_clear_flag,
               t.is_squadDay       tmp_is_squadDay,
               t.table_columns     tmp_table_columns
          from st_cfg_clear_table t
         where t.clear_flag = -1; --
    begin
    
      for i_cur in scc_table loop
      
        origin_table_name := trim(i_cur.tmp_origin_table);
        table_columns     := trim(i_cur.tmp_table_columns);
        begin_time        := sysdate;
        keep_days         := i_cur.tmp_keep_days;
        --查询当前表当天流水号的最小运营日
        begin
          v_sql := ' select min(squad_day) from ' || origin_table_name ||
                   ' where balance_water_no = ''' || center_balance_no || '''';
          execute immediate v_sql
            into minDate;
        EXCEPTION
          when others then
            begin
              rollback;
              out_retresult  := SQLCODE;
              out_msg        := '向临时表t#st_sts_inc_day_min中插入数据错误：错误代码： ' ||
                                SQLERRM || ',发生在第[' ||
                                dbms_utility.format_error_backtrace() || ']行';
              v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
              up_st_ist_settle_log_add(origin_table_name,
                                       '0',
                                       center_balance_no,
                                       begin_time,
                                       sysdate,
                                       v_cost_seconds,
                                       0,
                                       out_msg,
                                       v_sql);
              return;
            end;
        end;
        -- select min_squadDate into minDate from t#st_sts_inc_day_min;
        --delete from t#st_sts_inc_day_min;
      
        --首先查询要导入的最大历史表名
        v_sql := ' select max(table_name)   from st_idx_rpt_history where origin_table_name =''' ||
                 origin_table_name || '''';
        execute immediate v_sql
          into max_table;
        --再更新最大历史表的最大运营日期为当天
        update st_idx_rpt_history
           set max_squad_day = to_char(sysdate, 'yyyymmdd')
         where table_name = max_table;
      
        --查询最小历史表
        v_sql := ' select  min(table_name) from st_idx_rpt_history where origin_table_name =''' ||
                 origin_table_name || '''and max_squad_day >=''' || minDate || '''';
        execute immediate v_sql
          into min_table;
      
        --循环向分表中插入数据
        begin
          declare
            cursor index_table is
              select table_name    temp_table_name,
                     min_squad_day temp_min_squad_day,
                     max_squad_day temp_max_squad_day
                from st_idx_rpt_history t
               where origin_table_name = origin_table_name
                 and table_name >= min_table
                 and table_name <= max_table;
          
          begin
            for t_cur in index_table loop
              --如果当前清算日期已经迁移过数据
              v_sql := 'select count(*) from ' || t_cur.temp_table_name ||
                       ' where balance_water_no=''' || center_balance_no || '''';
              execute immediate v_sql
                into v_count;
              if v_count > 0 then
                out_retresult := SQLCODE;
                out_msg       := '清算日期[' || center_balance_no ||
                                 ']对应的数据已经迁移致历史表';
                return;
              end if;
              begin_time := sysdate;
              -- 根据运营日插入对应的历史表
            
              begin
                v_sql := ' insert into ' || t_cur.temp_table_name ||
                         ' select ' || table_columns || ' from ' ||
                         origin_table_name || ' where squad_day >=''' ||
                         t_cur.temp_min_squad_day ||
                         ''' and squad_day <=''' ||
                         t_cur.temp_max_squad_day ||
                         ''' and balance_water_no =''' || center_balance_no || '''';
                execute immediate v_sql;
              EXCEPTION
                when others then
                  begin
                    rollback;
                    out_retresult  := SQLCODE;
                    out_msg        := origin_table_name || '插入历史表错误,错误代码:' ||
                                      SQLERRM || ',发生在第[' ||
                                      dbms_utility.format_error_backtrace() || ']行';
                    v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
                    up_st_ist_settle_log_add(origin_table_name,
                                             '0',
                                             center_balance_no,
                                             begin_time,
                                             sysdate,
                                             v_cost_seconds,
                                             0,
                                             out_msg,
                                             v_sql);
                    return;
                  end;
                
              end;
            
              begin
                v_sql := 'select count(*) from ' || origin_table_name ||
                         ' where squad_day >=''' || t_cur.temp_min_squad_day ||
                         ''' and squad_day <=''' ||
                         t_cur.temp_max_squad_day ||
                         ''' and balance_water_no =''' || center_balance_no || '''';
                execute immediate v_sql
                  into v_count;
              EXCEPTION
                when others then
                  begin
                    rollback;
                    out_retresult  := SQLCODE;
                    out_msg        := '向临时表t#st_sts_inc_count插入数据错误,错误代码:' ||
                                      SQLERRM || ',发生在第[' ||
                                      dbms_utility.format_error_backtrace() || ']行';
                    v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
                    up_st_ist_settle_log_add(origin_table_name,
                                             '0',
                                             center_balance_no,
                                             begin_time,
                                             sysdate,
                                             v_cost_seconds,
                                             0,
                                             out_msg,
                                             v_sql);
                    return;
                  end;
              end;
              begin
                --select cnt into v_count from t#st_sts_inc_count;
                --delete from t#st_sts_inc_count;
              
                --更新st_idx_rpt_history 历史记录表信息
                update st_idx_rpt_history
                   set recd_count     = recd_count + v_count,
                       end_balance_no = center_balance_no
                 where table_name = t_cur.temp_table_name;
              
                v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
                out_msg        := '从' || origin_table_name || '表中导入到' ||
                                  t_cur.temp_table_name || '历史表的数据成功';
                up_st_ist_settle_log_add(origin_table_name,
                                         t_cur.temp_table_name,
                                         center_balance_no,
                                         begin_time,
                                         sysdate,
                                         v_cost_seconds,
                                         0,
                                         out_msg,
                                         v_sql);
              
              end;
            
            end loop;
          end;
        end;
      
      end loop;
    end;
    -------------------------迁移数据结束---------------------------
    -------------------------删除数据开始---------------------------
    begin
      --删除配置表中此中间表保存天数前的数据
      begin_time := sysdate;
      begin
        v_sql := ' select count(*) from ' || origin_table_name ||
                 'where balance_water_no <''' ||
                 to_char(sysdate - keep_days, 'yyyymmdd') || '01''';
        execute immediate v_sql
          into v_count;
      EXCEPTION
        when others then
          begin
            rollback;
            out_retresult  := SQLCODE;
            out_msg        := '向临时表t#st_sts_inc_count插入数据错误,错误代码:' ||
                              SQLERRM || ',发生在第[' ||
                              dbms_utility.format_error_backtrace() || ']行';
            v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
            up_st_ist_settle_log_add(origin_table_name,
                                     '0',
                                     center_balance_no,
                                     begin_time,
                                     sysdate,
                                     v_cost_seconds,
                                     0,
                                     out_msg,
                                     v_sql);
            return;
          end;
      end;
      select cnt into v_count from t#st_sts_inc_count;
      delete from t#st_sts_inc_count;
      --删除
      begin
        v_sql := 'delete from ' || origin_table_name ||
                 ' where balance_water_no <''' ||
                 to_char(sysdate - keep_days, 'yyyymmdd') || '01''';
        execute immediate v_sql;
      EXCEPTION
        when others then
          begin
            rollback;
            out_retresult  := SQLCODE;
            out_msg        := '删除' || origin_table_name ||
                              '表中导入历史表的数据错误,错误代码:' || SQLERRM || ',发生在第[' ||
                              dbms_utility.format_error_backtrace() || ']行';
            v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
            up_st_ist_settle_log_add(origin_table_name,
                                     '0',
                                     center_balance_no,
                                     begin_time,
                                     sysdate,
                                     v_cost_seconds,
                                     0,
                                     out_msg,
                                     v_sql);
            return;
          end;
      end;
      --记录日志
      out_msg        := '删除' || origin_table_name || '表中清理天数前的数据成功';
      v_cost_seconds := ROUND(TO_NUMBER(sysdate - begin_time) * 24 * 60 * 60);
      up_st_ist_settle_log_add(origin_table_name,
                               '0',
                               center_balance_no,
                               begin_time,
                               sysdate,
                               v_cost_seconds,
                               0,
                               out_msg,
                               v_sql);
    
    end;
    -------------------------删除数据结束---------------------------
  
  end;
  out_retresult := SQLCODE;
  out_msg       := '数据迁移成功';
  commit;
exception
  when OTHERS THEN
    begin
      ROLLBACK;
      out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                       dbms_utility.format_error_backtrace() || ']行';
      out_retResult := sqlcode;
      return;
    end;
  
END up_st_ist_settle_rptsquaday_mv;
/
grant execute on ACC_ST.UP_ST_IST_SETTLE_RPTSQUADAY_MV to ACC_ST_APP;


