prompt
CREATE OR REPLACE PROCEDURE ACC_ST.up_st_ist_settle_rpt_create(out_retResult OUT INTEGER, --返回结果
                                                        out_msg       OUT VARCHAR2 --返回信息
                                                        )
---------------------------------------------------------------------------------
  --过程名：  UP_ST_IST_SETTLE_HIS_MORE
  --功能描述：建立中央清算各中间表的历史分表
  --输出参数  ：out_retResult 模块返回值 out_msg 返回信息
  --返回值    ：0：操作成功；其他：操作失败；out_retResult为0时，out_msg返回表名
  --创建者：  张建华
  --创建日期：201301023
  --修改：
  -------------------------------------------------------------------------------
 AS

  v_msg VARCHAR(50);
  --v_sql              varchar(5000);
  --v_new_begin date; --新的开始时间
  --v_new_end          date; --新的结束时间
  v_min_time varchar2(10); --最小运营时间
  v_max_time varchar2(10); --最大运营时间

  v_min_balance_no varchar2(10); --最大清算流水号
  v_max_balance_no varchar2(10); -- 最小清算流水
  v_max_table      varchar2(40); --最大的历史表
  v_count          number(18); --临时统计变量
  v_count2         number(18); --临时统计变量
  v_flag           number(1); --创建001的标志，0：创建  
  v_time           varchar2(20);
  v_temp_sql       varchar2(4000); --临时sql语句
  v_table_sql      varchar2(4000); --建表临时sql语句
  v_temp_num       varchar2(3); --分表序列号
  new_table_name   varchar2(40); --分表表名
BEGIN
  begin
    declare
      cursor scc_table is
        select t.origin_table_name tmp_origin_table,
               t.create_sql        tmp_create_sql,
               t.is_squadday       tmp_is_squadday,
               t.index_name        tmp_index_name,
               t.pk_name           tmp_pk_name
          from st_cfg_clear_table t;
    begin
      for i_cur in scc_table loop
        begin
          select count(*)
            into v_count
            from st_idx_rpt_history
           where table_name like i_cur.tmp_pk_name || '_%';
        end;
        begin
          --因为有些原表名太长，所以生成的分表采用自定义的表名，控制了长度
          new_table_name := i_cur.tmp_pk_name || '_001';
        
          v_temp_sql := 'select count(*) from user_objects where object_name = upper (''' ||
                        new_table_name || ''')';
          execute immediate v_temp_sql
            into v_count2;
        end;
        begin
          if v_count = 0 then
            --st_idx_rpt_history没有对应分表的记录
            if v_count2 > 0 then
              --分表的物理表存在
              v_temp_sql := 'drop table ' || new_table_name;
              execute immediate v_temp_sql; --删除001分表表
            
            end if;
            v_flag := 0;
          
          else
            ----st_idx_rpt_history有对应分表的记录
            if v_count2 = 0 then
              --分表的物理表不存在
              v_flag := 0;
            end if;
          end if;
        end;
        if v_flag = 0 then
          --先建表，再初始化
          begin
            --建001表
            begin
              --获取建表语句
              --替换表名
              select REGEXP_REPLACE(i_cur.tmp_create_sql,
                                    '%s',
                                    new_table_name)
                into v_temp_sql
                from dual;
              --替换索引
              select REGEXP_REPLACE(v_temp_sql,
                                    '%i',
                                    i_cur.tmp_index_name || '_001')
                into v_temp_sql
                from dual;
              --替换主键
              select REGEXP_REPLACE(v_temp_sql,
                                    '%p',
                                    i_cur.tmp_pk_name || '_001')
                into v_temp_sql
                from dual;
            
              --将建表语句插入到测试表，调试用
              insert into t_test
              values
                (to_char(systimestamp, 'yyyy-mm-dd hh24:mi:ss.ff'),
                 v_temp_sql);
              commit;
              --每个建表语句中含有建索引、主键信息，且以；分割
              declare
                cursor sql_cur is
                  select * from table(splitstr(v_temp_sql, ';'));
              begin
                for t_cur in sql_cur loop
                  v_table_sql := t_cur.column_value;
                  insert into t_test
                  values
                    (to_char(systimestamp, 'yyyy-mm-dd hh24:mi:ss.ff'),
                     v_table_sql);
                  commit;
                
                  if trim(v_table_sql) is not null and v_table_sql <> ' ' then
                    execute immediate v_table_sql; --创建001分表相关的操作
                  end if;
                end loop;
              end;
            end;
          
            select to_char(sysdate - 1, 'yyyymmdd') into v_time from dual;
          
            --计算最小、最大流水号、最小运营日，最大运营日
            if i_cur.tmp_is_squadday = 0 then
              v_temp_sql := 'select min(t.balance_water_no),max(t.balance_water_no) from ' ||
                            i_cur.tmp_origin_table ||
                            ' t where t.balance_water_no <''' || v_time ||
                            '01''';
              execute immediate v_temp_sql
                into v_min_balance_no, v_max_balance_no;
              v_min_time := '19790101';
              v_max_time := '19790101';
            else
              --计算最小、最大流水号、最小运营日，最大运营日
            
              v_temp_sql := 'select min(t.balance_water_no),max(t.balance_water_no),min(t.squad_day),max(t.squad_day)
                  from ' ||
                            i_cur.tmp_origin_table ||
                            ' t where t.balance_water_no <''' || v_time || '''';
              execute immediate v_temp_sql
                into v_min_balance_no, v_max_balance_no, v_min_time, v_max_time;
            
            end if;
            begin
              select replace(v_min_time, ' ', '')
                into v_min_time
                from dual;
              select replace(v_max_time, ' ', '')
                into v_max_time
                from dual;
              select replace(v_min_balance_no, ' ', '')
                into v_min_balance_no
                from dual;
              select replace(v_max_balance_no, ' ', '')
                into v_max_balance_no
                from dual;
            end;
          
            if v_min_balance_no is null or v_min_balance_no = '' then
              v_min_balance_no := v_time || '01';
            end if;
          
            if v_max_balance_no is null or v_max_balance_no = '' then
              v_max_balance_no := v_time || '01';
            end if;
          
            select count(*)
              into v_count
              from st_idx_rpt_history
             where table_name = new_table_name;
            if v_count = 0 then
              insert into st_idx_rpt_history
                (table_name,
                 origin_table_name,
                 begin_balance_no,
                 end_balance_no,
                 min_squad_day,
                 max_squad_day,
                 recd_count)
              values
                (new_table_name,
                 i_cur.tmp_origin_table,
                 v_min_balance_no,
                 v_max_balance_no,
                 v_min_time,
                 v_max_time,
                 0);
            end if;
          
          end;
        end if;
      end loop;
    
    end;
  
  end;

  ---------------以下为创建非001表

  begin
    declare
      cursor cu_table is
        select regexp_substr(a.table_name, '.{1,3}$') tmp_i_list_no,
               a.recd_count tmp_recd_count,
               to_char(to_date(substr(a.end_balance_no, 0, 8), 'yyyymmdd') + 1,
                       'yyyymmdd') || '01' tmp_max_balance_no,
               to_char(to_date(a.max_squad_day, 'yyyymmdd') + 1, 'yyyymmdd') tmp_max_squad_day,
               a.origin_table_name tmp_origin_table,
               b.divide_recd_count tmp_divide_recd,
               b.create_sql tmp_create_sql,
               b.is_squadday tmp_is_squadDay,
               b.index_name tmp_index_name,
               b.pk_name tmp_pk_name
          from st_idx_rpt_history a, st_cfg_clear_table b
         where a.origin_table_name = b.origin_table_name
           and a.table_name in
               (select max(table_name)
                  from st_idx_rpt_history
                 group by origin_table_name);
    begin
      for my_cur in cu_table loop
        begin
          --取最大表
          select max(table_name)
            into v_max_table
            from st_idx_rpt_history
           where origin_table_name = my_cur.tmp_origin_table
           group by origin_table_name;
          --获取当前表
          select regexp_substr(v_max_table, '.{1,3}$')
            into v_temp_num
            from dual;
        end;
      
        begin
          begin
            if my_cur.tmp_recd_count >= my_cur.tmp_divide_recd then
              --达到配置表中的最大记录数，需要建表
              --下一分表的序列号
              v_temp_num     := up_st_ist_settle_his_num(my_cur.tmp_i_list_no);
              new_table_name := my_cur.tmp_pk_name || '_' || v_temp_num;
              --获取建表语句              
              --替换表名
              select REGEXP_REPLACE(my_cur.tmp_create_sql,
                                    '%s',
                                    new_table_name)
                into v_table_sql
                from dual;
              --替换索引
              select REGEXP_REPLACE(v_table_sql,
                                    '%i',
                                    my_cur.tmp_index_name || v_temp_num)
                into v_temp_sql
                from dual;
              --替换主键
              select REGEXP_REPLACE(v_temp_sql,
                                    '%p',
                                    my_cur.tmp_pk_name || v_temp_num)
                into v_temp_sql
                from dual;
            
              --将建表语句插入到测试表，调试用
              insert into t_test
              values
                (to_char(systimestamp, 'yyyy-mm-dd hh24:mi:ss.ff'),
                 v_temp_sql);
              commit;
              --每个建表语句中含有建索引、主键信息，且以；分割
              declare
                cursor sql_cur is
                  select * from table(splitstr(v_temp_sql, ';'));
              begin
                for t_cur in sql_cur loop
                  v_table_sql := t_cur.column_value;
                  insert into t_test
                  values
                    (to_char(systimestamp, 'yyyy-mm-dd hh24:mi:ss.ff'),
                     v_table_sql);
                  commit;
                  if trim(v_table_sql) is not null and v_table_sql <> ' ' then
                    execute immediate v_table_sql; --创建001分表相关的操作
                  end if;
                end loop;
              end;
            
              begin
                if my_cur.tmp_is_squadDay = 1 then
                  v_min_time := my_cur.tmp_max_squad_day;
                  v_max_time := my_cur.tmp_max_squad_day;
                else
                  v_min_time := '19790101';
                  v_max_time := '19790101';
                end if;
                begin
                  select count(*)
                    into v_count
                    from st_idx_rpt_history
                   where table_name = new_table_name;
                
                  insert into st_idx_rpt_history
                    (table_name,
                     origin_table_name,
                     begin_balance_no,
                     end_balance_no,
                     min_squad_day,
                     max_squad_day,
                     recd_count)
                  values
                    (new_table_name,
                     my_cur.tmp_origin_table,
                     my_cur.tmp_max_balance_no,
                     my_cur.tmp_max_balance_no,
                     v_min_time,
                     v_max_time,
                     0);
                end;
              end; --if上的begin    
            
            end if;
          end;
        
        end; --loop中的第2个begin
      end loop;
    end; -- for之前的begin
    out_msg       := '建表成功';
    out_retResult := sqlcode;
    commit;
  end; --declare前的begin

exception
  when OTHERS THEN
    begin
      ROLLBACK;
      out_msg       := v_msg || '错误：' || sqlerrm || ',发生在第[' ||
                       dbms_utility.format_error_backtrace() || ']行';
      out_retResult := sqlcode;
      return;
    end;
  
END up_st_ist_settle_rpt_create;
/
