							 where BALANCE_WATER_NO=piBalanceWaterNo and FILE_NAME=piFileName;
  --	清除缓存表数据					  
  DELETE FROM ST_BUF_REG_DTL where BALANCE_WATER_NO=piBalanceWaterNo and FILE_NAME=piFileName;
-------------------------------------------------------------------------------------------------------------------------  
 
  
  
  
   COMMIT;
   
   
   EXCEPTION
   WHEN OTHERS THEN
   BEGIN
      poRetCode :=-1;
      poRetMsg :='错误代码：'||sqlcode||' 错误消息：'||sqlerrm;
      ROLLBACK;
   END;
end;
