poRetCode :=0;
poRetMsg :='执行成功'||'清算流水：'||piBalanceWaterNo||' 文件名称:'||piFileName;
--获取测试票控制标识
select cfg_value into testControl from st_cfg_sys_base 
                                  where cfg_key='business.test.control' and record_flag='0';
--设置缺省值
if testControl ='' then
   testControl :='1';
end if;
---历史分表查重 缺省值不需查重
isNeedCheckHisSub :='0';
payModeId :='58';
----------------------------------------------------------------------------------
--校验
--重复性校验
--与队列表记录
--判断是否有重复记录
-------------------------------------重复校验------------------------------------------------------------------------
--当前队列表查重
insert into T#ST_BUF_NON_RTN_APP(
