						   FROM ACC_ST_HIS.'||tableName||
						   ')'
         using tableName;
         
      end loop;                                              
      --按分表名称查重（结束）                                                            
end if;
                     
---------------------历史表分表查重（结束）---------------------                          
                          
 --------------------------------------------------结束对列表、历史表查重-----------------------------------------------------------------                         
                           
                           
  SELECT COUNT(*) INTO rowCount FROM T#ST_BUF_NON_RTN_APP;
  IF rowCount != 0 THEN
     BEGIN
  --重复记录插入错误表					   
        insert into ST_ERR_NON_RTN_APP(
