   ------------------------------------------------结束导入队列表--------------------------------------------------------
  
 --	清除缓存表数据					  
  DELETE FROM ST_BUF_NON_RTN_APP where BALANCE_WATER_NO=piBalanceWaterNo and FILE_NAME=piFileName;
  
  
  
  
   COMMIT;
   
   
   EXCEPTION
   WHEN OTHERS THEN
   BEGIN
