prompt
CREATE OR REPLACE PROCEDURE ACC_ST."UP_ST_TRF_BUF_QUEUE_SALE"
(
piBalanceWaterNo in varchar2,
piFileName in varchar2,
poRetCode out int,
 poRetMsg out varchar2
)
---------------------------------------------------------------------------------
--过程名：  up_st_trf_buf_queue_sale
--功能描述：校验发售缓存表数据及导入到对应的队列表
--参数：piBalanceWaterNo：清算流水号
--      piFileName:交易文件名称
--      poRetCode：0：成功 -1：失败
--      poRetMsg：返回代码说明
--创建者：  hejj
--创建日期：20130807
--修改者: hejj
--修改日期: 20140430
--修改描述：增加测试票处理功能
--修改者: hejj
--修改日期: 20140501
--修改描述：增加当前历史表查重功能/文件内查重
--修改者: hejj
--修改日期: 20140501
--修改描述：增加历史表分表查重功能
--修改者: hejj
--修改日期: 20160801
--修改描述：记录重复改为业务重复，错误代码42改为50
--修改者: hejj
--修改日期: 20160815
--修改描述：增加缓存表自身查重
--修改者: hejj
--修改日期: 20170418
--修改描述:(1)历史表、历史表分表查重区分重编码旧卡、新卡、非重编码卡
--         （2）业务查重字段由逻辑卡号改为逻辑卡号+物理卡号
-------------------------------------------------------------------------------
is
rowCount int;
testControl varchar(1);
---历史分表查重
tableName varchar(30);--分表名称
dateMaxHis date;--分表的最大结束时间
isNeedCheckHisSub varchar2(1);--是否需要查询分表标识
payModeId  varchar(2);---交易类型
csr sys_refcursor;
isNeedCheckAgain varchar2(1);--是否需要查询重编码表标识
begin
poRetCode :=0;
poRetMsg :='执行成功'||'清算流水：'||piBalanceWaterNo||' 文件名称:'||piFileName;
--获取测试票控制标识
select cfg_value into testControl from st_cfg_sys_base
                                  where cfg_key='business.test.control' and record_flag='0';
--设置缺省值
if testControl ='' then
   testControl :='1';
end if;
---历史分表查重 缺省值不需查重
isNeedCheckHisSub :='0';
---重编码表查重 缺省值不需查
isNeedCheckAgain :='0';
payModeId :='51';
----------------------------------------------------------------------------------
--校验
--重复性校验
--与队列表记录
--判断是否有重复记录
-------------------------------------重复校验------------------------------------------------------------------------
--当前缓存表自身查重
--与文件名称小的记录比较，这样文件名称小的重复记录保留，文件名称大的重复记录删除
insert into T#ST_BUF_SALE(
                             WATER_NO ,LINE_ID,STATION_ID ,DEV_TYPE_ID,DEVICE_ID,CARD_MAIN_ID ,CARD_SUB_ID,CARD_LOGICAL_ID,
               CARD_PHYSICAL_ID ,CARD_STATUS_ID ,WATER_NO_BUSINESS,SAM_LOGICAL_ID ,SAM_TRADE_SEQ,DEPOSIT_TYPE ,
               DEPOSIT_FEE,SALE_DATETIME,RECEIPT_ID ,OPERATOR_ID,SHIFT_ID ,AUXI_FEE ,CARD_APP_FLAG,BALANCE_WATER_NO ,
               FILE_NAME,CHECK_FLAG
                            )
          SELECT   WATER_NO ,LINE_ID,STATION_ID ,DEV_TYPE_ID,DEVICE_ID,CARD_MAIN_ID ,CARD_SUB_ID,CARD_LOGICAL_ID,
               CARD_PHYSICAL_ID ,CARD_STATUS_ID ,WATER_NO_BUSINESS,SAM_LOGICAL_ID ,SAM_TRADE_SEQ,DEPOSIT_TYPE ,
               DEPOSIT_FEE,SALE_DATETIME,RECEIPT_ID ,OPERATOR_ID,SHIFT_ID ,AUXI_FEE ,CARD_APP_FLAG,BALANCE_WATER_NO ,
               FILE_NAME,CHECK_FLAG
          FROM ST_BUF_SALE A
          WHERE A.FILE_NAME=piFileName AND A.BALANCE_WATER_NO=piBalanceWaterNo AND
                          --改为业务重复判断20160801
                          EXISTS
              (
                           SELECT 1
               FROM ST_BUF_SALE B
                           WHERE B.FILE_NAME < piFileName AND B.BALANCE_WATER_NO=piBalanceWaterNo AND
                                 A.CARD_LOGICAL_ID=B.CARD_LOGICAL_ID AND A.CARD_PHYSICAL_ID=B.CARD_PHYSICAL_ID
               );
--当前队列表查重
insert into T#ST_BUF_SALE(
                             WATER_NO ,LINE_ID,STATION_ID ,DEV_TYPE_ID,DEVICE_ID,CARD_MAIN_ID ,CARD_SUB_ID,CARD_LOGICAL_ID,
               CARD_PHYSICAL_ID ,CARD_STATUS_ID ,WATER_NO_BUSINESS,SAM_LOGICAL_ID ,SAM_TRADE_SEQ,DEPOSIT_TYPE ,
               DEPOSIT_FEE,SALE_DATETIME,RECEIPT_ID ,OPERATOR_ID,SHIFT_ID ,AUXI_FEE ,CARD_APP_FLAG,BALANCE_WATER_NO ,
               FILE_NAME,CHECK_FLAG
                            )
          SELECT   WATER_NO ,LINE_ID,STATION_ID ,DEV_TYPE_ID,DEVICE_ID,CARD_MAIN_ID ,CARD_SUB_ID,CARD_LOGICAL_ID,
               CARD_PHYSICAL_ID ,CARD_STATUS_ID ,WATER_NO_BUSINESS,SAM_LOGICAL_ID ,SAM_TRADE_SEQ,DEPOSIT_TYPE ,
               DEPOSIT_FEE,SALE_DATETIME,RECEIPT_ID ,OPERATOR_ID,SHIFT_ID ,AUXI_FEE ,CARD_APP_FLAG,BALANCE_WATER_NO ,
               FILE_NAME,CHECK_FLAG
