  --当前历史表查重
insert into ACC_ST.T#ST_BUF_SALE_SJT(
                             WATER_NO ,LINE_ID,STATION_ID ,DEV_TYPE_ID,DEVICE_ID,
                              SAM_LOGICAL_ID ,SAM_TRADE_SEQ,SALE_DATETIME,PAY_TYPE ,CARD_LOGICAL_ID_PAY,
