                CARD_APP_FLAG,TAC_DEAL_TYPE,TAC_DEV_ID
          FROM ACC_ST.ST_BUF_SALE_SJT a
          WHERE --FILE_NAME=piFileName AND BALANCE_WATER_NO=piBalanceWaterNo AND
