   ------------------------------------------------结束导入队列表--------------------------------------------------------
 --  清除缓存表数据
  DELETE FROM ACC_ST.ST_BUF_SALE_SJT where BALANCE_WATER_NO=piBalanceWaterNo and FILE_NAME=piFileName;
   COMMIT;
   EXCEPTION
   WHEN OTHERS THEN
   BEGIN
      poRetCode :=-1;
      poRetMsg :='错误代码：'||sqlcode||' 错误消息：'||sqlerrm;
      ROLLBACK;
   END;
end;
/
