mapper.select:
select 

from w_acc_st.W_OP_CFG_SYS
where TYPE = #{type,jdbcType=VARCHAR}
  and TYPE_SUB = #{typeSub,jdbcType=VARCHAR}
  and CONFIG_NAME = #{configName,jdbcType=VARCHAR}

select 

from w_acc_st.W_OP_CFG_SYS
where TYPE = #{type,jdbcType=VARCHAR}
  and TYPE_SUB = #{typeSub,jdbcType=VARCHAR}

mapper.delete:
delete from w_acc_st.W_OP_CFG_SYS
where TYPE = #{type,jdbcType=VARCHAR}
  and TYPE_SUB = #{typeSub,jdbcType=VARCHAR}
  and CONFIG_NAME = #{configName,jdbcType=VARCHAR}

mapper.update:
update w_acc_st.W_OP_CFG_SYS

  
    TYPE_NAME = #{typeName,jdbcType=VARCHAR},
  
  
    TYPE_SUB_NAME = #{typeSubName,jdbcType=VARCHAR},
  
  
    CONFIG_VALUE = #{configValue,jdbcType=VARCHAR},
  
  
    REMARK = #{remark,jdbcType=VARCHAR},
  

where TYPE = #{type,jdbcType=VARCHAR}
  and TYPE_SUB = #{typeSub,jdbcType=VARCHAR}
  and CONFIG_NAME = #{configName,jdbcType=VARCHAR}

update w_acc_st.W_OP_CFG_SYS
set TYPE_NAME = #{typeName,jdbcType=VARCHAR},
  TYPE_SUB_NAME = #{typeSubName,jdbcType=VARCHAR},
  CONFIG_VALUE = #{configValue,jdbcType=VARCHAR},
  REMARK = #{remark,jdbcType=VARCHAR}
where TYPE = #{type,jdbcType=VARCHAR}
  and TYPE_SUB = #{typeSub,jdbcType=VARCHAR}
  and CONFIG_NAME = #{configName,jdbcType=VARCHAR}
