  set IS_SEND = '1'
where CLASS_SIMPLE_NAME = #{classSimpleName ,jdbcType=VARCHAR} and IP = #{ip,jdbcType=VARCHAR} and PASRE_TIME = #{pasreTime,jdbcType=VARCHAR}

mapper.select:
select count(1) from w_acc_mn.w_mtr_email_contents
where class_simple_name = #{classSimpleName ,jdbcType=VARCHAR}
and ip = #{ip,jdbcType=VARCHAR} and is_handle = '0'

    and key =#{key,jdbcType=VARCHAR}

select count(1) from w_acc_mn.w_mtr_email_contents
